import { Routes, Route, NavLink } from "react-router-dom";
import ProductManager from "./ProductManager";
import OrdersPanel from "./OrdersPanel";
import UsersPanel from "./UsersPanel";

export default function AdminPanel() {
  return (
    <div style={{ display: "flex", minHeight: "100vh", backgroundColor: "#f3f4f6" }}>
      {/* –ë–æ–∫–æ–≤–∞—è –ø–∞–Ω–µ–ª—å */}
      <aside style={{
        width: "220px",
        backgroundColor: "#1f2937",
        color: "#fff",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        gap: "15px"
      }}>
        <h2 style={{ fontSize: "20px", marginBottom: "20px" }}>üîê Admin</h2>

        <NavLink
          to="/admin/products"
          style={linkStyle}
          className={({ isActive }) => isActive ? "active" : ""}
        >
          üì¶ –¢–æ–≤–∞—Ä—ã
        </NavLink>
        <NavLink
          to="/admin/orders"
          style={linkStyle}
          className={({ isActive }) => isActive ? "active" : ""}
        >
          üßæ –ó–∞–∫–∞–∑—ã
        </NavLink>
        <NavLink
          to="/admin/users"
          style={linkStyle}
          className={({ isActive }) => isActive ? "active" : ""}
        >
          üë§ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏
        </NavLink>
      </aside>

      {/* –û—Å–Ω–æ–≤–Ω–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ */}
      <main style={{ flex: 1, backgroundColor: "#fff", padding: "30px" }}>
        <Routes>
          <Route path="/admin/products" element={<ProductManager />} />
          <Route path="/admin/orders" element={<OrdersPanel />} />
          <Route path="/admin/users" element={<UsersPanel />} />
        </Routes>
      </main>
    </div>
  );
}

const linkStyle = ({ isActive }) => ({
  padding: "10px 15px",
  borderRadius: "6px",
  textDecoration: "none",
  color: isActive ? "#10b981" : "#fff",
  backgroundColor: isActive ? "#374151" : "transparent",
  transition: "0.2s"
});
