// Header.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const [isCatalogOpen, setIsCatalogOpen] = useState(false);
  const catalogRef = useRef(null);

  // Категории для выпадающего меню
  const categories = [
    "Электроника",
    "Одежда",
    "Бытовая техника",
    "Красота",
    "Спорт",
    "Детские товары",
    "Книги"
  ];

  // Закрытие меню при клике вне области
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (catalogRef.current && !catalogRef.current.contains(e.target)) {
        setIsCatalogOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <header className="header">
      <div className="logo">
        <Link to="/">
          <h1>COOLZY</h1>
        </Link>
      </div>

      <div className="search">
        <input
          type="text"
          className="search_box"
          placeholder=" ПОИСК"
        />
      </div>

      <nav className="navigation">
        {/* Кнопка каталога с выпадающим меню */}
        <div className="catalog-wrapper" ref={catalogRef}>
          <button
            className="catalog"
            onClick={() => setIsCatalogOpen(!isCatalogOpen)}
          >
<svg xmlns="http://www.w3.org/2000/svg" width="26" height="18" viewBox="0 0 26 18" fill="none">
<rect width="2" height="2" rx="1" fill="#454545"/>
<rect x="6" width="20" height="2" rx="1" fill="#454545"/>
<rect y="8" width="2" height="2" rx="1" fill="#454545"/>
<rect x="6" y="8" width="20" height="2" rx="1" fill="#454545"/>
<rect y="16" width="2" height="2" rx="1" fill="#454545"/>
<rect x="6" y="16" width="20" height="2" rx="1" fill="#454545"/>
</svg>
            <span className="icon_text">Каталог</span>
          </button>

          {/* Выпадающее меню */}
          <div className={`catalog-menu ${isCatalogOpen ? 'show' : ''}`}>
            {categories.map((category, index) => (
              <Link
                to={`/category/${category}`}
                key={index}
                className="menu-item"
              >
                {category}
              </Link>
            ))}
          </div>
        </div>

        {/* Корзина (без изменений) */}
        <Link to="/cart" className="basket">
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none">
<path d="M9 22.5C9.55228 22.5 10 22.0523 10 21.5C10 20.9477 9.55228 20.5 9 20.5C8.44772 20.5 8 20.9477 8 21.5C8 22.0523 8.44772 22.5 9 22.5Z" stroke="#454545" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
<path d="M19 22.5C19.5523 22.5 20 22.0523 20 21.5C20 20.9477 19.5523 20.5 19 20.5C18.4477 20.5 18 20.9477 18 21.5C18 22.0523 18.4477 22.5 19 22.5Z" stroke="#454545" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
<path d="M1 1.5H5L7.68 15.7823C7.77144 16.2734 8.02191 16.7145 8.38755 17.0285C8.75318 17.3424 9.2107 17.5092 9.68 17.4996H19.4C19.8693 17.5092 20.3268 17.3424 20.6925 17.0285C21.0581 16.7145 21.3086 16.2734 21.4 15.7823L23 6.8332H6" stroke="#454545" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
</svg>
          <span className="icon_text">Корзина</span>
        </Link>

        {/* Профиль (оригинальная версия) */}
        <Link to="/profile" className="profile">
        <svg xmlns="http://www.w3.org/2000/svg" width="22" height="24" viewBox="0 0 22 24" fill="none">
<path d="M21 23V20.6667C21 19.429 20.4732 18.242 19.5355 17.3668C18.5979 16.4917 17.3261 16 16 16H6C4.67392 16 3.40215 16.4917 2.46447 17.3668C1.52678 18.242 1 19.429 1 20.6667V23" stroke="#454545" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
<path d="M10.5 11C13.2614 11 15.5 8.76142 15.5 6C15.5 3.23858 13.2614 1 10.5 1C7.73858 1 5.5 3.23858 5.5 6C5.5 8.76142 7.73858 11 10.5 11Z" stroke="#454545" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
</svg>

          <span className="icon_text">Профиль</span>
        </Link>
      </nav>
    </header>
  );
}

export default Header;
