import React from 'react';
import './Category.css';
import { useNavigate } from 'react-router-dom';

const categoryImages = {
  Смартфоны: '/img/category/huaweipura70.jpg',
  Ноутбуки: '/img/category/macbook.jpg',
  Планшеты: '/img/category/tablet.jpg',
  Телевизоры: '/img/category/TVHorizont.jpg',
  Колонки: '/img/category/jblgo4.jpg',
};

const Category = () => {
  const categories = ['Смартфоны', 'Ноутбуки', 'Планшеты', 'Телевизоры', 'Колонки'];
  const navigate = useNavigate();

  const handleCategoryClick = (category) => {
    // переход на путь категории
    navigate(`/category/${category}`);
  };

  return (
    <div className="category-wrapper">
      <h3 className="category-title">Категории</h3>
      <div className="category-container">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => handleCategoryClick(category)}
            className="category-card"
          >
            <img src={categoryImages[category]} alt={category} />
            <p>{category}</p>
          </button>
        ))}
      </div>
    </div>
  );
};

export default Category;
