import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Checkout.css";

const Checkout = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [formData, setFormData] = useState({
    fullName: "",
    phone: "",
    address: "",
    paymentMethod: "Карта",
  });

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return navigate("/login");

    fetch(`http://localhost:5085/api/cart/${storedUser.userId}`)
      .then(res => res.json())
      .then(data => setCartItems(data))
      .catch(err => console.error("Ошибка загрузки корзины", err));
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return;

    const orderItems = cartItems.map(item => ({
      productId: item.productId,
      quantity: item.quantity,
      price: item.product?.newPrice || item.product?.price || 0,
    }));

    const orderData = {
      userId: storedUser.userId,
      fullName: formData.fullName,
      phone: formData.phone,
      address: formData.address,
      paymentMethod: formData.paymentMethod,
      items: orderItems,
    };

    try {
      const res = await fetch("http://localhost:5085/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderData),
      });

      if (res.ok) {
        const data = await res.json();
        navigate(`/order-success?orderId=${data.orderId}`);
      } else {
        const err = await res.text();
        alert("Ошибка при создании заказа: " + err);
      }
    } catch (err) {
      alert("Ошибка сервера");
    }
  };

  return (
    <div className="checkout-wrapper">
      <h2>Оформление заказа</h2>
      <form className="checkout-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="fullName"
          placeholder="ФИО"
          value={formData.fullName}
          onChange={handleInputChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Телефон"
          value={formData.phone}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Адрес доставки"
          value={formData.address}
          onChange={handleInputChange}
          required
        />
        <select
          name="paymentMethod"
          value={formData.paymentMethod}
          onChange={handleInputChange}
        >
          <option value="Карта">Картой</option>
          <option value="Наличные">Наличные</option>
        </select>

        <button type="submit" className="checkout-btn">
          Подтвердить заказ
        </button>
      </form>
    </div>
  );
};

export default Checkout;
