import React, { useState, useEffect } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import "../CategoryPage/CategoryPage";
import { getProductsByCategory } from "../../api"; // твой API

const Tablets = () => {
  const [tablets, setTablets] = useState([]);
  const [filters, setFilters] = useState({
    brand: "",
    screenSize: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === "reset") {
      setFilters({
        brand: "",
        screenSize: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  useEffect(() => {
    const fetchTablets = async () => {
      const data = await getProductsByCategory("Планшеты", filters);
      setTablets(data);
    };
    fetchTablets();
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Планшеты" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог планшетов</h1>
        <div className="product-grid">
          {tablets.map((tablet) => (
            <Link to={`/products/${tablet.id}`} key={tablet.id} className="product-link">
              <ProductCard
                id={tablet.id}
                img={tablet.img}
                model={tablet.model}
                oldPrice={tablet.oldPrice}
                newPrice={tablet.newPrice}
                availability={tablet.availability}
                discount={
                  tablet.oldPrice
                    ? Math.round((1 - tablet.newPrice / tablet.oldPrice) * 100)
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tablets;
