import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./ProfilePage.css";

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) return;
    setUser(storedUser);

    // –ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ
    fetch(`http://localhost:5085/api/favorite/user/${storedUser.userId}`)
      .then((res) => res.json())
      .then((data) => setFavorites(data))
      .catch((err) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ:", err));

    // –ó–∞–≥—Ä—É–∑–∫–∞ –∑–∞–∫–∞–∑–æ–≤
    fetch(`http://localhost:5085/api/orders/user/${storedUser.userId}`)
      .then((res) => res.json())
      .then(async (orderData) => {
        const parsedOrders = await Promise.all(
          orderData.map(async (order) => {
            let pdfUrl = null;
            let contractUrl = `http://localhost:5085/api/orders/contract/${order.orderId}`;

            try {
              const pdfRes = await fetch(`http://localhost:5085/api/orders/pdf/${order.orderId}`);
              const pdfData = await pdfRes.json();
              pdfUrl = "http://localhost:5085" + pdfData.pdfUrl;
            } catch {}

            return {
              id: order.orderId,
              date: order.createdAt,
              pdfUrl,
              contractUrl,
              totalPrice: order.items?.reduce((sum, item) => sum + item.price * item.quantity, 0) || 0,
              items: order.items?.map((item) => ({
                id: item.orderItemId,
                productId: item.product?.productId || item.productId,
                name: item.product?.model || item.product?.name || "–¢–æ–≤–∞—Ä",
                image: item.product?.imageUrl || "/img/placeholder.png",
                price: item.price || 0,
                quantity: item.quantity || 1,
              })) || [],
            };
          })
        );
        setOrders(parsedOrders);
      })
      .catch((err) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–∫–∞–∑–æ–≤:", err));
  }, []);

  return (
    <div className="profile-container">
      <h2>–ü—Ä–æ—Ñ–∏–ª—å</h2>

      {user && (
        <div className="profile-info">
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:</strong> {user.username}</p>
        </div>
      )}

      <div className="profile-section">
        <h3>–ò–∑–±—Ä–∞–Ω–Ω–æ–µ</h3>
        {favorites.length === 0 ? (
          <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –∏–∑–±—Ä–∞–Ω–Ω—ã—Ö —Ç–æ–≤–∞—Ä–æ–≤.</p>
        ) : (
          <ul className="favorites-list">
            {favorites.map((product) => (
              <li key={product.productId} onClick={() => navigate(`/products/${product.productId}`)}>
                <img src={product.imageUrl} alt={product.model} />
                <span>{product.brand} {product.model}</span>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="profile-section">
        <h3>–ú–æ–∏ –∑–∞–∫–∞–∑—ã</h3>
        {orders.length === 0 ? (
          <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –∑–∞–∫–∞–∑–æ–≤.</p>
        ) : (
          <div className="orders-wrapper">
            {orders.map(order => (
              <div key={order.id} className="order-card">
                <div className="order-header">
                  <div>
                    <h4>–ó–∞–∫–∞–∑ ‚Ññ{order.id}</h4>
                    <div className="order-date">{new Date(order.date).toLocaleDateString()}</div>
                  </div>
                  <div className="order-sum">
                    {order.totalPrice.toFixed(2)} BYN
                  </div>
                </div>

                <div className="order-items">
                  {order.items.map(item => (
                    <div key={item.id} className="order-item" onClick={() => navigate(`/products/${item.productId}`)}>
                      <img src={item.image} alt={item.name} className="order-image" />
                      <div className="order-info">
                        <div className="order-title">{item.name}</div>
                        <div className="order-qty">√ó {item.quantity}</div>
                      </div>
                      <div className="order-price">
                        {(item.price * item.quantity).toFixed(2)} BYN
                      </div>
                    </div>
                  ))}
                </div>

                <div className="order-actions">
                  {order.pdfUrl && (
                    <a href={order.pdfUrl} target="_blank" rel="noreferrer">üìÑ –ì–∞—Ä–∞–Ω—Ç–∏—è</a>
                  )}
                  <a href={order.contractUrl} target="_blank" rel="noreferrer">üìÉ –î–æ–≥–æ–≤–æ—Ä</a>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
