import { useEffect, useState } from "react";
import axios from "axios";
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Title, Tooltip, Legend);

export default function ReportPage() {
  const [orders, setOrders] = useState([]);
  const [productsCount, setProductsCount] = useState(0);

  useEffect(() => {
    axios.get("http://localhost:5085/api/orders").then(res => setOrders(res.data));
    axios.get("http://localhost:5085/api/products").then(res => setProductsCount(res.data.length));
  }, []);

  const handleExportOrders = () => {
    window.open("http://localhost:5085/api/admin/reports/orders", "_blank");
  };

  const handleExportUsers = () => {
    window.open("http://localhost:5085/api/admin/reports/users", "_blank");
  };

  const getChartData = () => {
    const grouped = {};

    orders.forEach(order => {
      const date = new Date(order.orderDate || order.createdAt).toLocaleDateString();
      grouped[date] = (grouped[date] || 0) + 1;
    });

    const labels = Object.keys(grouped).sort((a, b) => new Date(a) - new Date(b));
    const data = labels.map(label => grouped[label]);

    return {
      labels,
      datasets: [
        {
          label: '–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–∫–∞–∑–æ–≤',
          data,
          fill: false,
          borderColor: '#3b82f6',
          tension: 0.2
        }
      ]
    };
  };

  return (
    <div style={{ padding: "30px" }}>
      <h2>üìä –û—Ç—á—ë—Ç—ã</h2>

      {/* –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ç–æ–≤–∞—Ä–æ–≤ */}
      <div style={{ marginTop: 20, marginBottom: 30 }}>
        <h3>üì¶ –û–±—â–µ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ç–æ–≤–∞—Ä–æ–≤: {productsCount}</h3>
      </div>

      {/* –ì—Ä–∞—Ñ–∏–∫ –∑–∞–∫–∞–∑–æ–≤ */}
      <div style={{ marginBottom: 40 }}>
        <h3>üìà –ì—Ä–∞—Ñ–∏–∫ –∑–∞–∫–∞–∑–æ–≤ –ø–æ –¥–∞—Ç–µ</h3>
        <Line data={getChartData()} />
      </div>

      {/* –ö–Ω–æ–ø–∫–∏ —ç–∫—Å–ø–æ—Ä—Ç–∞ */}
      <div style={{ display: "flex", flexDirection: "column", gap: 15 }}>
        <button onClick={handleExportOrders} style={btn}>üì• –°–∫–∞—á–∞—Ç—å Excel ‚Äî –ó–∞–∫–∞–∑—ã</button>
        <button onClick={handleExportUsers} style={btn}>üì• –°–∫–∞—á–∞—Ç—å Excel ‚Äî –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</button>
      </div>
    </div>
  );
}

const btn = {
  backgroundColor: "#2563eb",
  color: "#fff",
  padding: "12px 20px",
  fontSize: "16px",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  maxWidth: "300px"
};
