import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css'; // подключи при необходимости

const ProfilePage = () => {
  const [userData, setUserData] = useState({ username: '', email: '', password: '' });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser || !storedUser.userId) {
      navigate("/login");
      return;
    }

    fetch(`http://localhost:5085/api/users/${storedUser.userId}`)
      .then(res => res.json())
      .then(data => {
        setUserData({
          username: data.username || '',
          email: data.email || '',
          password: data.password || ''
        });
        setLoading(false);
      })
      .catch(err => {
        console.error("Ошибка загрузки данных профиля:", err);
        setLoading(false);
      });
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/users/${storedUser.userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id: storedUser.userId,
          ...userData
        })
      });

      if (res.ok) {
        const updated = await res.json();
        localStorage.setItem("user", JSON.stringify(updated));
        alert("Профиль обновлён");
      } else {
        const errText = await res.text();
        console.error("Ошибка обновления профиля:", errText);
        alert("Ошибка при обновлении профиля");
      }
    } catch (err) {
      console.error("Ошибка:", err);
      alert("Ошибка при сохранении");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (loading) return <div>Загрузка...</div>;

  return (
    <div className="profile-page">
      <h2>Профиль</h2>

      <div className="form-group">
        <label>Имя пользователя</label>
        <input
          type="text"
          name="username"
          value={userData.username}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={userData.email}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Пароль</label>
        <input
          type="password"
          name="password"
          value={userData.password}
          onChange={handleChange}
        />
      </div>

      <button className="save-btn" onClick={handleSave}>Сохранить</button>
      <button className="logout-btn" onClick={handleLogout}>Выйти</button>
    </div>
  );
};

export default ProfilePage;
