// Category.jsx
import React from "react";
import { Link } from "react-router-dom";
import "./Category.css";

const categories = [
  { title: "Смартфоны", image: "/img/category/huaweipura70.jpg", link: "/category/Смартфоны" },
  { title: "Ноутбуки", image: "/img/category/macbook.jpg", link: "/category/Ноутбуки" },
  { title: "Планшеты", image: "/img/category/tablet.jpg", link: "/category/Планшеты" },
  { title: "Телевизоры", image: "/img/category/TVHorizont.jpg", link: "/category/Телевизоры" },
  { title: "Колонки", image: "/img/category/jblgo4.jpg", link: "/category/Колонки" },
];

const Category = ({ setCategory }) => {
  const handleCategoryClick = (category) => {
    setCategory(category); // Устанавливаем выбранную категорию
  };

  return (
    <div className="category-wrapper">
      <h2 className="category-title">Каталог</h2>
      <div className="category-container">
        {categories.map((cat, index) => (
          <Link
            to={cat.link}
            key={index}
            className="category-card"
            onClick={() => handleCategoryClick(cat.title)}
          >
            <img src={cat.image} alt={cat.title} />
            <p>{cat.title}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Category;
