import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css';

const ProfilePage = () => {
  const [userData, setUserData] = useState({ username: '', email: '', password: '' });
  const [orders, setOrders] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser || !storedUser.userId) {
      navigate("/login");
      return;
    }

    const fetchProfile = async () => {
      try {
        const [userRes, ordersRes, favRes] = await Promise.all([
          fetch(`http://localhost:5085/api/users/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/orders/user/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/favorite/${storedUser.userId}`)
        ]);

        const userData = await userRes.json();
        const orderData = await ordersRes.json();
        const favoriteData = await favRes.json();

        setUserData({
          username: userData.username || '',
          email: userData.email || '',
          password: userData.password || ''
        });
        setOrders(orderData);
        setFavorites(favoriteData);
        setLoading(false);
      } catch (err) {
        console.error("Ошибка загрузки профиля:", err);
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/users/${storedUser.userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id: storedUser.userId,
          ...userData
        })
      });

      if (res.ok) {
        const updated = await res.json();
        localStorage.setItem("user", JSON.stringify(updated));
        alert("Профиль обновлён");
      } else {
        const errText = await res.text();
        console.error("Ошибка обновления профиля:", errText);
        alert("Ошибка при обновлении профиля");
      }
    } catch (err) {
      console.error("Ошибка:", err);
      alert("Ошибка при сохранении");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (loading) return <div>Загрузка...</div>;

  return (
    <div className="profile-page">
      <h2>Профиль</h2>

      <div className="form-group">
        <label>Имя пользователя</label>
        <input type="text" name="username" value={userData.username} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label>Email</label>
        <input type="email" name="email" value={userData.email} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label>Пароль</label>
        <input type="password" name="password" value={userData.password} onChange={handleChange} />
      </div>

      <button className="save-btn" onClick={handleSave}>Сохранить</button>
      <button className="logout-btn" onClick={handleLogout}>Выйти</button>

      <div className="section">
        <h3>Мои заказы</h3>
        {orders.length === 0 ? (
          <p>У вас нет заказов.</p>
        ) : (
          <ul>
            {orders.map(order => (
              <li key={order.id}>Заказ №{order.id} от {new Date(order.date).toLocaleDateString()} — {order.totalPrice} BYN</li>
            ))}
          </ul>
        )}
      </div>

      <div className="section">
        <h3>Избранное</h3>
        {favorites.length === 0 ? (
          <p>Нет избранных товаров.</p>
        ) : (
          <ul>
            {favorites.map(fav => (
              <li key={fav.productId}>{fav.product?.model || 'Неизвестный товар'}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
