// Cart.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      name: "Смартфон Apple iPhone 14 128GB",
      image: "https://store.storeimages.cdn-apple.com/4668/as-images.apple.com/is/iphone-14-finish-select-202209-6-1inch-blue?wid=5120&hei=2880&fmt=p-jpg&qlt=80&.v=1661026582322",
      screen: "6.1 (2532x1170)",
      cores: 6,
      power: "20 Вт",
      price: 84999,
      discount: 2500,
      quantity: 1,
      available: true
    },
    {
      id: 2,
      name: "Смартфон Apple iPhone 14 128GB",
      image: "https://store.storeimages.cdn-apple.com/4668/as-images.apple.com/is/iphone-14-finish-select-202209-6-1inch-blue?wid=5120&hei=2880&fmt=p-jpg&qlt=80&.v=1661026582322",
      screen: "6.1 (2532x1170)",
      cores: 6,
      power: "20 Вт",
      price: 84999,
      discount: 2500,
      quantity: 1,
      available: true
    }
  ]);

  const navigate = useNavigate();

  const handleRemove = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleQuantityChange = (id, amount) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = Math.max(1, item.quantity + amount);
        return { ...item, quantity: newQuantity };
      }
      return item;
    }));
  };

  const calculateTotal = () => {
    return cartItems.reduce(
      (sum, item) => sum + (item.price - (item.discount || 0)) * item.quantity,
      0
    );
  };

  if (cartItems.length === 0) {
    return (
      <div className="cart-container">
        <h2>Моя корзина</h2>
        <div className="cart-empty">
          <p>Ваша корзина пуста</p>
          <button
            className="catalog-btn"
            onClick={() => navigate('/')}
          >
            Перейти в каталог
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Моя корзина</h2>
      <div className="cart-grid">
        {cartItems.map(item => (
          <div key={item.id} className="cart-item">
            <div className="cart-image-wrapper">
              {item.discount > 0 && (
                <div className="discount-badge">
                  -{Math.round((item.discount / (item.price + item.discount)) * 100)}%
                </div>
              )}
              {item.available && (
                <div className="availability-badge in-stock">
                  В наличии
                </div>
              )}
              <img
                src={item.image || 'https://via.placeholder.com/120x120?text=No+Image'}
                alt={item.name}
                className="cart-image"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/120x120?text=No+Image';
                }}
              />
            </div>
            <div className="cart-content">
              <h3 className="cart-title">{item.name}</h3>
              <p>Экран: {item.screen}</p>
              <p>Количество ядер: {item.cores}</p>
              <p>Мощность блока питания: {item.power}</p>
              <div className="price-wrapper">
                {item.discount > 0 && (
                  <div className="old-price">
                    {(item.price + item.discount).toLocaleString()} BYN
                  </div>
                )}
                <div className="new-price">
                  {item.price.toLocaleString()} BYN
                </div>
                <div className="quantity-control">
                  <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
                </div>
              </div>
            </div>
            <div className="cart-footer">
              <button
                className="add-to-cart-btn"
                onClick={() => handleQuantityChange(item.id, 1)}
              >
                +1
              </button>
              <button
                className={`favorite-button ${false ? 'active' : ''}`} // Пока без логики избранного
                onClick={(e) => e.stopPropagation()}
              >
                <svg width="24" height="24" viewBox="0 0 24 24">
                  <path
                    d="M17.612 2.826C17.172 2.405 16.65 2.072 16.075 1.844C15.5 1.617 14.884 1.5 14.262 1.5C13.64 1.5 13.024 1.617 12.449 1.844C11.874 2.072 11.352 2.405 10.912 2.826L10 3.698L9.087 2.826C8.198 1.977 6.994 1.5 5.737 1.5C4.48 1.5 3.276 1.977 2.388 2.826C1.499 3.675 1 4.826 1 6.027C1 7.227 1.499 8.378 2.388 9.227L3.3 10.099L10 16.5L16.699 10.099L17.612 9.227C18.052 8.807 18.401 8.308 18.639 7.759C18.877 7.21 19 6.621 19 6.027C19 5.432 18.877 4.843 18.639 4.294C18.401 3.745 18.052 3.246 17.612 2.826Z"
                    fill="none"
                    stroke="#0C0C0C"
                    strokeWidth="2"
                  />
                </svg>
              </button>
            </div>
            <button
              className="remove-btn"
              onClick={() => handleRemove(item.id)}
              title="Удалить"
            >
              ×
            </button>
          </div>
        ))}
      </div>
      <div className="cart-summary">
        <div className="summary-row">
          <span>Итого:</span>
          <span>{calculateTotal().toLocaleString()} BYN</span>
        </div>
        <button
          className="checkout-btn"
          onClick={handleCheckout}
        >
          Перейти к оформлению
        </button>
      </div>
    </div>
  );
};

export default Cart;
