import { useEffect, useState } from "react";
import axios from "axios";

export default function UsersPanel() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get("/api/admin/users")
      .then(res => setUsers(res.data))
      .catch(err => console.error("Ошибка при загрузке пользователей", err));
  }, []);

  const handleDelete = (id) => {
    if (!window.confirm("Удалить пользователя?")) return;

    axios.delete(`/api/admin/users/${id}`)
      .then(() => {
        setUsers(prev => prev.filter(u => u.userId !== id));
      })
      .catch(err => console.error("Ошибка при удалении", err));
  };

  const handleMakeAdmin = (id) => {
    axios.post(`/api/admin/set-admin/${id}`)
      .then(res => {
        setUsers(prev => prev.map(u => u.userId === id ? { ...u, isAdmin: true } : u));
      })
      .catch(err => console.error("Ошибка при назначении администратора", err));
  };

  return (
    <div>
      <h2 style={{ fontSize: "24px", fontWeight: "600", marginBottom: "20px" }}>Список пользователей</h2>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ backgroundColor: "#f0f0f0" }}>
            <th style={{ padding: "10px", textAlign: "left" }}>ID</th>
            <th style={{ padding: "10px", textAlign: "left" }}>Имя</th>
            <th style={{ padding: "10px", textAlign: "left" }}>Email</th>
            <th style={{ padding: "10px", textAlign: "left" }}>Роль</th>
            <th style={{ padding: "10px", textAlign: "left" }}>Действия</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.userId} style={{ borderBottom: "1px solid #ccc" }}>
              <td style={{ padding: "10px" }}>{u.userId}</td>
              <td style={{ padding: "10px" }}>{u.username}</td>
              <td style={{ padding: "10px" }}>{u.email}</td>
              <td style={{ padding: "10px" }}>{u.isAdmin ? "Админ" : "Пользователь"}</td>
              <td style={{ padding: "10px" }}>
                <button onClick={() => handleDelete(u.userId)} style={{ marginRight: "10px", color: "white", background: "red", border: "none", padding: "5px 10px", borderRadius: "4px" }}>
                  Удалить
                </button>
                {!u.isAdmin && (
                  <button onClick={() => handleMakeAdmin(u.userId)} style={{ background: "green", color: "white", border: "none", padding: "5px 10px", borderRadius: "4px" }}>
                    Сделать админом
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
