import React from "react";


const Category = () => {
  const categories = [
    { img: "img/category/macbook.jpg", name: "Ноутбуки", route: "/category/laptops" },
    { img: "img/category/huaweipura70.jpg", name: "Смартфоны", route: "/category/smartphones" },
    { img: "img/category/TVHorizont.jpg", name: "Телевизоры", route: "/category/tvs" },
    { img: "img/category/notepad.jpg", name: "Планшеты", route: "/category/tablets" },
    { img: "img/category/jblgo4.jpg", name: "Колнки", route: "/category/speakers" },
  ];

  return (
    <div className="category">
      <div className="category_title">Каталог</div>
      <div className="category_list">
        {categories.map((category, index) => (
          // Оборачиваем карточку в Link и указываем путь для перехода через prop to
          <Link to={category.route} key={index} className="category_link">
            <div className="category_card">
              <div className="category_img">
                <img src={category.img} alt={category.name} />
              </div>
              <div className="category_opis">{category.name}</div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Category;
