import React, { useEffect, useState } from 'react';
import ProductCard from './';

const Smartphones = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5085/api/products/category/Смартфоны')
      .then(res => res.json())
      .then(data => {
        console.log("Полученные товары:", data);
        setProducts(data);
      })
      .catch(err => console.error("Ошибка загрузки:", err));
  }, []);

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
      {products.map(p => (
        <ProductCard
          key={p.productId}
          id={p.productId}
          model={p.model}
          oldPrice={p.oldPrice}
          newPrice={p.newPrice}
          img={p.images?.[0]?.imageUrl} // Если у товара есть изображения
          availability={p.availability}
          discount={
            p.oldPrice > p.newPrice
              ? Math.round(((p.oldPrice - p.newPrice) / p.oldPrice) * 100)
              : 0
          }
        />
      ))}
    </div>
  );
};

export default Smartphones;
