import { useEffect, useState } from "react";
import axios from "axios";

export default function ProductManager() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  const [form, setForm] = useState({
    model: "",
    brand: "",
    newPrice: "",
    availability: "–í –Ω–∞–ª–∏—á–∏–∏",
    categoryId: "",
    image: null,
  });

  useEffect(() => {
    loadProducts();
    loadCategories();
  }, []);

  const loadProducts = () => {
    axios.get("http://localhost:5085/api/products")
      .then(res => setProducts(res.data))
      .catch(console.error);
  };

  const loadCategories = () => {
    axios.get("http://localhost:5085/api/categories")
      .then(res => setCategories(res.data))
      .catch(console.error);
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      setForm({ ...form, image: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.model || !form.brand || !form.newPrice || !form.categoryId || !form.image) {
      return alert("–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è –∏ –∑–∞–≥—Ä—É–∑–∏—Ç–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ");
    }

    const data = new FormData();
    data.append("model", form.model);
    data.append("brand", form.brand);
    data.append("newPrice", form.newPrice);
    data.append("availability", form.availability);
    data.append("categoryId", form.categoryId);
    data.append("image", form.image);

    try {
      await axios.post("http://localhost:5085/api/products/add", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("–¢–æ–≤–∞—Ä –¥–æ–±–∞–≤–ª–µ–Ω!");
      setForm({ model: "", brand: "", newPrice: "", availability: "–í –Ω–∞–ª–∏—á–∏–∏", categoryId: "", image: null });
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ —Ç–æ–≤–∞—Ä–∞");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å —Ç–æ–≤–∞—Ä?")) return;

    try {
      await axios.delete(`http://localhost:5085/api/products/${id}`);
      loadProducts();
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏", err);
    }
  };

  return (
    <div style={{ maxWidth: 900 }}>
      <h2 style={{ marginBottom: 20 }}>üì¶ –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏</h2>

      <form onSubmit={handleSubmit} style={{ display: "grid", gap: 12, marginBottom: 30 }}>
        <input type="text" name="model" placeholder="–ú–æ–¥–µ–ª—å" value={form.model} onChange={handleChange} />
        <input type="text" name="brand" placeholder="–ë—Ä–µ–Ω–¥" value={form.brand} onChange={handleChange} />
        <input type="number" name="newPrice" placeholder="–¶–µ–Ω–∞" value={form.newPrice} onChange={handleChange} />
        <select name="availability" value={form.availability} onChange={handleChange}>
          <option value="–í –Ω–∞–ª–∏—á–∏–∏">–í –Ω–∞–ª–∏—á–∏–∏</option>
          <option value="–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏">–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏</option>
        </select>
        <select name="categoryId" value={form.categoryId} onChange={handleChange}>
          <option value="">-- –í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é --</option>
          {categories.map(cat => (
            <option key={cat.categoryId} value={cat.categoryId}>
              {cat.name}
            </option>
          ))}
        </select>
        <input type="file" name="image" accept="image/*" onChange={handleChange} />
        <button type="submit">–î–æ–±–∞–≤–∏—Ç—å —Ç–æ–≤–∞—Ä</button>
      </form>

      <h3>–°–ø–∏—Å–æ–∫ —Ç–æ–≤–∞—Ä–æ–≤</h3>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ background: "#f0f0f0" }}>
            <th>ID</th>
            <th>–ú–æ–¥–µ–ª—å</th>
            <th>–ë—Ä–µ–Ω–¥</th>
            <th>–¶–µ–Ω–∞</th>
            <th>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
            <th>–î–µ–π—Å—Ç–≤–∏—è</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.productId}>
              <td>{p.productId}</td>
              <td>{p.model}</td>
              <td>{p.brand}</td>
              <td>{p.newPrice} —Ä—É–±.</td>
              <td>{p.category?.name || "-"}</td>
              <td>
                <button onClick={() => handleDelete(p.productId)} style={{ background: "red", color: "#fff", border: "none", padding: "5px 10px", borderRadius: 4 }}>
                  –£–¥–∞–ª–∏—Ç—å
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
