import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) return;

    fetch(`http://localhost:5085/api/favorite/${user.userId}`)
      .then(res => res.json())
      .then(setFavorites);
  }, []);

  if (favorites.length === 0) {
    return <p style={{ textAlign: "center", fontSize: "18px" }}>Нет избранных товаров</p>;
  }

  return (
    <div>
      <h2 style={{ marginBottom: "20px" }}>Избранные товары</h2>
      <div className="cart-grid">
        {favorites.map(fav => (
          <div
            key={fav.id}
            className="cart-item"
            onClick={() => navigate(`/products/${fav.productId}`)}
            style={{ cursor: "pointer" }}
          >
            <div className="cart-image-wrapper">
              <img
                src={fav.product.images?.[0]?.imageUrl || "/img/placeholder.png"}
                alt={fav.product.name}
                className="cart-image"
              />
            </div>
            <h3 className="cart-title">{fav.product.name}</h3>
            <div className="new-price">{fav.product.price.toLocaleString()} BYN</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Favorites;
