import React from 'react';
import './ProductCard.css';
import CustomButton from "../CustomButton/CustomButton";

const ProductCard = ({
  img,
  model,
  oldPrice,
  newPrice,
  discount,
  availability
}) => {
  const calculateDiscount = () => {
    if (!oldPrice || oldPrice <= newPrice) return null;
    return Math.round(((oldPrice - newPrice) / oldPrice) * 100);
  };

  const discountValue = calculateDiscount() || discount;

  // Упрощаем обработку класса availability
  const availabilityClass = availability
    ? availability.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, '')
    : '';

  return (
    <div className="product-card">
      <div className="product-image-wrapper">
        {discountValue && (
          <div className="discount-badge">
            -{discountValue}%
          </div>
        )}
        <img src={img} alt={model} className="product-image" />
      </div>

      <div className="product-content">
        <h3 className="product-title">{model}</h3>

        <div className="price-wrapper">
          {oldPrice && oldPrice > newPrice && (
            <div className="old-price">
              {oldPrice.toLocaleString()} BYN
            </div>
          )}
          <div className="new-price">
            {newPrice.toLocaleString()} BYN
          </div>
        </div>

        <div className="product-footer">
          <span className={`availability ${availabilityClass}`}>
            {availability}
          </span>
          <CustomButton>
            <svg className="cart-icon" viewBox="0 0 24 24">
              <path d="M6 2L3 6v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V6l-3-4H6zm15 4l-3-4H6L3 6v12h18V6z"/>
              <path d="M16 10a4 4 0 1 1-8 0"/>
            </svg>
            В корзину
          </CustomButton>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
