import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      const user = JSON.parse(localStorage.getItem("user"));
      if (!user?.userId) return;

      try {
        const res = await fetch(`http://localhost:5085/api/cart/${user.userId}`);
        const json = await res.json();

        // Обработка разных форматов ответа
        const data = Array.isArray(json) ? json : (json.items || []);

        const parsed = data.map(item => ({
          id: item.id,
          name: item.product?.model || "Без названия",
          image: item.product?.images?.[0]?.imageUrl || "/img/placeholder.png",
          price: item.product?.newPrice ?? 0,
          quantity: item.quantity ?? 1,
          productId: item.product?.productId || item.productId
        }));

        setCartItems(parsed);
      } catch (err) {
        console.error("Ошибка загрузки корзины", err);
      }
    };

    fetchCart();
  }, []);

  const handleRemove = async (id) => {
    try {
      await fetch(`http://localhost:5085/api/cart/${id}`, { method: "DELETE" });
      setCartItems(prev => prev.filter(item => item.id !== id));
    } catch (err) {
      console.error("Ошибка удаления из корзины", err);
    }
  };

  const handleQuantityChange = async (id, change) => {
    const item = cartItems.find(i => i.id === id);
    if (!item) return;
    const newQty = Math.max(1, item.quantity + change);

    try {
      await fetch(`http://localhost:5085/api/cart/update/${id}`, {
        method: "PUT",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ quantity: newQty })
      });

      setCartItems(prev =>
        prev.map(i => i.id === id ? { ...i, quantity: newQty } : i)
      );
    } catch (err) {
      console.error("Ошибка обновления количества", err);
    }
  };

  const calculateTotal = () =>
    cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
  const user = JSON.parse(localStorage.getItem("user"));
  if (!user?.userId || cartItems.length === 0) {
    alert("Вы не вошли в систему или корзина пуста.");
    return;
  }

  const validItems = cartItems.filter(item =>
    Number.isInteger(item.productId) && item.quantity > 0
  );

  if (validItems.length === 0) {
    alert("Некорректные товары в корзине. Очистите или перезагрузите страницу.");
    return;
  }

  try {
    const response = await fetch("http://localhost:5085/api/orders", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        userId: user.userId,
        items: validItems.map(item => ({
          productId: item.productId,
          quantity: item.quantity
        }))
      })
    });

    if (response.ok) {

      setCartItems([]);
      navigate("/с");
    } else {
      const err = await response.text();
      alert("Ошибка оформления: " + err);
    }
  } catch (err) {
    console.error("Ошибка при отправке заказа:", err);
    alert("Что-то пошло не так.");
  }
};


  return (
    <div className="cart-container">
      <h2>Моя корзина</h2>
      <div className="cart-grid">
        {cartItems.map(item => (
          <div key={item.id} className="cart-item">
            <div className="cart-image-wrapper">
              <img src={item.image} alt={item.name} className="cart-image" />
            </div>
            <div className="cart-content">
              <h3 className="cart-title">{item.name}</h3>
              <div className="price-wrapper">
                <div className="new-price">{item.price.toLocaleString()} BYN</div>
                <div className="quantity-control">
                  <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
                </div>
              </div>
            </div>
            <button className="remove-btn" onClick={() => handleRemove(item.id)} title="Удалить">×</button>
          </div>
        ))}
      </div>

      <div className="cart-summary">
        <div className="summary-row">
          <span>Итого:</span>
          <span>{calculateTotal().toLocaleString()} BYN</span>
        </div>
        <button className="checkout-btn" onClick={handleCheckout}>
          Перейти к оформлению
        </button>
      </div>
    </div>
  );
};

export default Cart;
