import { useEffect, useState } from "react";
import axios from "axios";

export default function UsersPanel() {
  const [users, setUsers] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    load();
  }, []);

  const load = () => {
    axios.get("http://localhost:5085/api/admin/users")
      .then(res => {
        setUsers(res.data);
        setFiltered(res.data);
      });
  };

  const handleSearch = () => {
    const query = search.toLowerCase().trim();
    setFiltered(users.filter(u =>
      u.email.toLowerCase().includes(query) ||
      u.username.toLowerCase().includes(query)
    ));
  };

  return (
    <div style={{ maxWidth: "1000px", margin: "0 auto", padding: "30px" }}>
      <h2>👤 Пользователи</h2>

      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Поиск по email или имени..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button onClick={handleSearch} style={{ marginLeft: 8 }}>🔍</button>
        <button onClick={load} style={{ marginLeft: 8 }}>🔄 Сброс</button>
      </div>

      <table style={{ width: "100%", borderCollapse: "collapse", fontSize: "14px" }}>
        <thead>
          <tr style={{ backgroundColor: "#f3f4f6" }}>
            <th style={th}>ID</th>
            <th style={th}>Email</th>
            <th style={th}>Имя</th>
            <th style={th}>Аватар</th>
            <th style={th}>Действия</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map(u => (
            <tr key={u.userId}>
              <td style={td}>{u.userId}</td>
              <td style={td}>{u.email}</td>
              <td style={td}>{u.username}</td>
              <td style={td}>
                <img src={u.avatar || "/img/placeholder.png"} alt="avatar" width={40} height={40} style={{ borderRadius: "50%" }} />
              </td>
              <td style={td}>
                <button onClick={() => {
                  if (window.confirm("Удалить пользователя?"))
                    axios.delete(`http://localhost:5085/api/admin/users/${u.userId}`).then(load);
                }} style={deleteBtn}>🗑</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

const th = { padding: "10px", borderBottom: "2px solid #ddd", textAlign: "left" };
const td = { padding: "10px", borderBottom: "1px solid #eee" };
const deleteBtn = {
  background: "#ef4444", color: "#fff", border: "none", padding: "6px 10px",
  borderRadius: "6px", cursor: "pointer"
};
