import React, { useEffect, useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
import '../CategoryPage/CategoryPage.css';

const Tablets = () => {
  const [products, setProducts] = useState([]);
  const [filters, setFilters] = useState({ brand: '', ram: '', storage: '', minPrice: '', maxPrice: '' });

  const handleFilterChange = (name, value) => {
    setFilters(prev => name === 'reset' ? { brand: '', ram: '', storage: '', minPrice: '', maxPrice: '' } : { ...prev, [name]: value });
  };

  useEffect(() => {
    const query = new URLSearchParams(filters).toString();
    fetch(`http://localhost:5085/api/products/category/Планшеты?${query}`)
      .then(res => res.json())
      .then(setProducts)
      .catch(err => console.error('Ошибка загрузки:', err));
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Планшеты" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог планшетов</h1>
        <div className="product-grid">
          {products.map(p => (
            <ProductCard
              key={p.id}
              id={p.id}
              img={p.img}
              model={p.model}
              oldPrice={p.oldPrice}
              newPrice={p.newPrice}
              availability={p.availability}
              discount={p.oldPrice > p.newPrice ? Math.round((1 - p.newPrice / p.oldPrice) * 100) : null}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tablets;
