// Checkout.jsx
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  const [cartItems, setCartItems] = useState(state?.cartItems || []);
  const [total, setTotal] = useState(state?.total || 0);
  const [orderId, setOrderId] = useState(state?.orderId || null);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [pdfUrl, setPdfUrl] = useState(null);
  const [formData, setFormData] = useState({
    fullName: "",
    address: "",
    phone: "",
    paymentMethod: "Наложенный платёж"
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmitOrder = async () => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId || cartItems.length === 0) {
      alert("Ошибка: корзина пуста или вы не вошли в систему.");
      return;
    }

    // Проверяем валидность данных формы
    if (!formData.fullName || !formData.address || !formData.phone) {
      alert("Пожалуйста, заполните все поля формы.");
      return;
    }

    try {
      // Предполагаем, что заказ уже создан в Cart.jsx, поэтому просто подтверждаем данные
      // Если нужно обновить заказ с данными формы (например, адрес доставки), делаем PUT-запрос
      const res = await fetch(`http://localhost:5085/api/orders/${orderId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: storedUser.userId,
          fullName: formData.fullName,
          address: formData.address,
          phone: formData.phone,
          paymentMethod: formData.paymentMethod
        })
      });

      if (!res.ok) {
        const err = await res.text();
        alert("Ошибка обновления заказа: " + err);
        return;
      }

      const result = await res.json();
      setPdfUrl("http://localhost:5085" + result.pdfUrl);
      setOrderPlaced(true);

      // Перенаправляем на OrderSuccess после успешного обновления
      setTimeout(() => {
        navigate("/order-success", {
          state: {
            orderId: orderId,
            pdfUrl: "http://localhost:5085" + result.pdfUrl,
            contractUrl: "http://localhost:5085/api/orders/contract/" + orderId // Предполагаемый URL договора
          }
        });
      }, 1000); // Задержка для отображения сообщения об успехе
    } catch (err) {
      console.error("Ошибка при обновлении заказа:", err);
      alert("Что-то пошло не так.");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Оформление заказа</h2>

      {!orderPlaced ? (
        <>
          <div style={{ marginBottom: 20 }}>
            <input
              type="text"
              name="fullName"
              placeholder="ФИО"
              value={formData.fullName}
              onChange={handleChange}
              style={{ display: "block", marginBottom: 10 }}
            />
            <input
              type="text"
              name="address"
              placeholder="Адрес доставки"
              value={formData.address}
              onChange={handleChange}
              style={{ display: "block", marginBottom: 10 }}
            />
            <input
              type="tel"
              name="phone"
              placeholder="Телефон"
              value={formData.phone}
              onChange={handleChange}
              style={{ display: "block", marginBottom: 10 }}
            />
            <select
              name="paymentMethod"
              value={formData.paymentMethod}
              onChange={handleChange}
              style={{ display: "block", marginBottom: 20 }}
            >
              <option>Наложенный платёж</option>
              <option>Картой онлайн</option>
            </select>
          </div>

          <h4>Товары в заказе:</h4>
          <ul>
            {cartItems.map(item => (
              <li key={item.productId}>
                ID: {item.productId}, Кол-во: {item.quantity}
              </li>
            ))}
          </ul>
          <p><b>Итого:</b> {total.toLocaleString()} руб.</p>

          <button onClick={handleSubmitOrder}>Оформить заказ</button>
        </>
      ) : (
        <div>
          <h3>✅ Заказ успешно оформлен!</h3>
          {pdfUrl && (
            <a
              href={pdfUrl}
              target="_blank"
              rel="noopener noreferrer"
              style={{
                display: "inline-block",
                padding: "10px 20px",
                backgroundColor: "#4CAF50",
                color: "white",
                borderRadius: "5px",
                marginTop: "15px",
                textDecoration: "none"
              }}
            >
              Скачать гарантийный талон (PDF)
            </a>
          )}
        </div>
      )}
    </div>
  );
};

export default Checkout;
