import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import "./OrderSuccess.css";

const OrderSuccess = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const orderId = searchParams.get("orderId");

  const [pdfUrl, setPdfUrl] = useState(null);
  const [contractUrl, setContractUrl] = useState(null);

  useEffect(() => {
    if (!orderId) return;

    fetch(`http://localhost:5085/api/orders/pdf/${orderId}`)
      .then(res => res.json())
      .then(data => {
        setPdfUrl(`http://localhost:5085${data.pdfUrl}`);
        setContractUrl(`http://localhost:5085/api/orders/contract/${orderId}`);
      })
      .catch(() => {
        // –ï—Å–ª–∏ PDF –Ω–µ —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞–Ω ‚Äî –≤—Å—ë —Ä–∞–≤–Ω–æ –æ—Ç–æ–±—Ä–∞–∂–∞–µ–º —Å—Å—ã–ª–∫—É –Ω–∞ –¥–æ–≥–æ–≤–æ—Ä
       setContractUrl(`http://localhost:5085/api/orders/contract/${orderId}`);
      });
  }, [orderId]);

  return (
    <div className="order-success-wrapper">
      <h2>üéâ –ó–∞–∫–∞–∑ —É—Å–ø–µ—à–Ω–æ –æ—Ñ–æ—Ä–º–ª–µ–Ω!</h2>
      <p>–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞: <strong>#{orderId}</strong></p>

      <div className="order-links">
        {pdfUrl && (
          <a
            href={pdfUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="link-btn"
          >
            üìÑ –ì–∞—Ä–∞–Ω—Ç–∏—è
          </a>
        )}
        {contractUrl && (
          <a
            href={contractUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="link-btn"
          >
            üìÉ –î–æ–≥–æ–≤–æ—Ä
          </a>
        )}
      </div>

      <button className="checkout-btn" onClick={() => navigate("/profile")}>
        –í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø—Ä–æ—Ñ–∏–ª—å
      </button>
    </div>
  );
};

export default OrderSuccess;
