import React, { useState, useEffect } from "react";
import "./Cart.css";
import axios from "axios";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [loading, setLoading] = useState(true);

  // Загрузка данных из БД
  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const response = await axios.get("/api/cart");
      setCartItems(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Ошибка при загрузке корзины:", error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="cart-loading">Загрузка...</div>;
  }

  if (orderPlaced) {
    return (
      <div className="order-success">
        <h2>Заказ успешно оформлен!</h2>
        <p>Спасибо за покупку.</p>
        <button onClick={() => setOrderPlaced(false)}>Вернуться к покупкам</button>
      </div>
    );
  }

  if (cartItems.length === 0) {
    return (
      <div className="cart-empty">
        <h2>Ваша корзина пуста</h2>
        <button className="catalog-btn">Перейти в каталог</button>
      </div>
    );
  }

  const handleRemove = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleQuantityChange = (id, amount) => {
    setCartItems(cartItems.map(item =>
      item.id === id ? { ...item, quantity: Math.max(1, item.quantity + amount) } : item
    ));
  };

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  if (cartItems.length === 0) {
    return <div className="cart-empty">Корзина пуста</div>;
  }

  return (
    <div className="cart-container">
      <h2>Корзина</h2>
      {cartItems.map(item => (
        <div key={item.id} className="cart-item">
          <span>{item.name}</span>
          <span>{item.price}₽</span>
          <div className="quantity-control">
            <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
          </div>
          <span>{item.price * item.quantity}₽</span>
          <button className="remove-btn" onClick={() => handleRemove(item.id)}>Удалить</button>
        </div>
      ))}
      <div className="cart-total">
        <strong>Итого: {total}₽</strong>
      </div>
      <button className="order-btn">Оформить заказ</button>
    </div>
  );

};




export default Cart;
