import React from 'react';
import './Filters.css';

const filterConfigs = {
'Смартфоны': [
{
type: 'select',
key: 'brand',
label: 'Бренд',
options: ['Apple', 'Samsung', 'Xiaomi', 'Huawei', 'Honor']
},
{
type: 'select',
key: 'ram',
label: 'Оперативная память',
options: ['2GB', '4GB', '6GB', '8GB', '12GB']
},
{
type: 'select',
key: 'storage',
label: 'Встроенная память',
options: ['64GB', '128GB', '256GB', '512GB', '1TB']
},
{
type: 'range',
key: 'price',
label: 'Цена',
min: 0,
max: 5000
}
],
'Ноутбуки': [
{
type: 'select',
key: 'brand',
label: 'Бренд',
options: ['Apple', 'Asus', 'Lenovo', 'HP', 'Dell']
},
{
type: 'select',
key: 'screenSize',
label: 'Диагональ экрана',
options: ['13"', '14"', '15.6"', '16"', '17.3"']
},
{
type: 'select',
key: 'processor',
label: 'Процессор',
options: ['Intel Core i3', 'Intel Core i5', 'Intel Core i7', 'Intel Core i9', 'AMD Ryzen 5', 'AMD Ryzen 7']
},
{
type: 'range',
key: 'price',
label: 'Цена',
min: 0,
max: 10000
}
],
'Планшеты': [
{
type: 'select',
key: 'brand',
label: 'Бренд',
options: ['Apple', 'Samsung', 'Huawei', 'Lenovo']
},
{
type: 'select',
key: 'screenSize',
label: 'Диагональ экрана',
options: ['8"', '10"', '11"', '12.9"']
},
{
type: 'range',
key: 'price',
label: 'Цена',
min: 0,
max: 4000
}
],
'Телевизоры': [
{
type: 'select',
key: 'brand',
label: 'Бренд',
options: ['Samsung', 'LG', 'Sony', 'Philips']
},
{
type: 'select',
key: 'screenSize',
label: 'Диагональ экрана',
options: ['32"', '43"', '50"', '55"', '65"', '75"']
},
{
type: 'select',
key: 'resolution',
label: 'Разрешение',
options: ['Full HD', '4K', '8K']
},
{
type: 'range',
key: 'price',
label: 'Цена',
min: 0,
max: 15000
}
],
'Колонки': [
{
type: 'select',
key: 'brand',
label: 'Бренд',
options: ['JBL', 'Sony', 'Bose', 'Harman Kardon']
},
{
type: 'select',
key: 'type',
label: 'Тип',
options: ['Портативные', 'Домашние', 'Саундбары']
},
{
type: 'range',
key: 'price',
label: 'Цена',
min: 0,
max: 2000
}
]
};

const Filters = ({ category, filters, onFilterChange }) => {
const categoryFilters = filterConfigs[category] || [];

const renderFilter = (filter) => {
switch (filter.type) {
case 'select':
return (
<div className="filter-group" key={filter.key}>
<label>{filter.label}:</label>
<select
value={filters[filter.key] || ''}
onChange={(e) => onFilterChange(filter.key, e.target.value)}
>
<option value="">Все</option>
{filter.options.map(option => (
<option key={option} value={option}>{option}</option>
))}
</select>
</div>
);
case 'range':
return (
<div className="filter-group" key={filter.key}>
<label>{filter.label}:</label>
<div className="price-inputs">
<input
type="number"
placeholder="От"
value={filters.minPrice || ''}
onChange={(e) => onFilterChange('minPrice', e.target.value)}
min={filter.min}
max={filter.max}
/>
<span>-</span>
<input
type="number"
placeholder="До"
value={filters.maxPrice || ''}
onChange={(e) => onFilterChange('maxPrice', e.target.value)}
min={filter.min}
max={filter.max}
/>
</div>
</div>
);
default:
return null;
}
};

return (
<div className="filters-panel">
<h3>Фильтры</h3>
{categoryFilters.map(renderFilter)}
</div>
);
};

export default Filters;
