import React, { useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
//import './Smartphones.css';
import { Link } from 'react-router-dom';

const Smartphones = () => {
 useEffect(() => {
  const fetchProducts = async () => {
    const query = new URLSearchParams({
      category: 'Смартфоны',
      brand: filters.brand,
      minPrice: filters.minPrice,
      maxPrice: filters.maxPrice
    });

    const res = await fetch(`/api/products?${query}`);
    const data = await res.json();
    setSmartphones(data);
  };

  fetchProducts();
}, [filters]);


  const [filters, setFilters] = useState({
    brand: '',
    availability: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (filterName, value) => {
    setFilters(prev => ({ ...prev, [filterName]: value }));
  };

  const filteredSmartphones = allSmartphones.filter(phone => {
    const price = phone.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;

    return (
      (!filters.brand || phone.brand === filters.brand) &&
      (!filters.availability || phone.availability === filters.availability) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="smartphones-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>

      <div className="smartphones-catalog">
        <h1>Каталог смартфонов</h1>
        <div className="product-grid">
          {filteredSmartphones.map(phone => (
            <Link
              to={`/products/${phone.id}`}
              key={phone.id}
              className="product-link"
            >
              <ProductCard
                {...phone}
                discount={phone.discount ? `${Math.round((1 - phone.newPrice/phone.oldPrice)*100)}%` : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
