// CheckoutPage.jsx
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Cart/';

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { cartItems = [], total = 0 } = location.state || {};

  const [address, setAddress] = useState("Минск, улица Колесникова, 3");
  const [paymentMethod, setPaymentMethod] = useState("cash");
  const [saveCard, setSaveCard] = useState(false);
  const [cardNumber, setCardNumber] = useState("");
  const [cardName, setCardName] = useState("");
  const [cardExpiry, setCardExpiry] = useState("");
  const [cardCvv, setCardCvv] = useState("");

  const handlePlaceOrder = () => {
    // В реальном приложении здесь будет отправка данных на сервер
    console.log("Order data:", {
      address,
      paymentMethod,
      cardInfo: paymentMethod === "card" ? {
        cardNumber,
        cardName,
        cardExpiry,
        cardCvv,
        saveCard
      } : null,
      items: cartItems,
      total
    });

    navigate('/order-success');
  };

  const handleBackToCart = () => {
    navigate('/cart');
  };

  if (!cartItems || cartItems.length === 0) {
    return (
      <div className="cart-container">
        <div className="cart-empty">
          <p>Ваша корзина пуста</p>
          <button
            className="catalog-btn"
            onClick={() => navigate('/')}
          >
            Перейти в каталог
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <button
        onClick={handleBackToCart}
        className="back-button"
        style={{
          background: 'none',
          border: 'none',
          color: '#2196F3',
          cursor: 'pointer',
          marginBottom: '20px',
          fontSize: '16px'
        }}
      >
        ← Вернуться в корзину
      </button>

      <h2>Оформление заказа</h2>

      <div className="cart-item">
        <div className="item-info">
          <h3>Куда привезти заказ?</h3>
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            className="checkout-input"
            placeholder="Введите адрес"
          />
          <p style={{ marginTop: "10px" }}>Послезавтра</p>
          <p>{cartItems.length} товар</p>
        </div>
      </div>

      <div className="cart-item">
        <div className="item-info">
          <h3>Как оплатить заказ?</h3>
          <p>Самое Популярное</p>

          <div style={{ marginTop: "15px" }}>
            <h4>Способ оплаты</h4>

            <div className="payment-method">
              <input
                type="radio"
                id="cash"
                name="payment"
                checked={paymentMethod === "cash"}
                onChange={() => setPaymentMethod("cash")}
              />
              <label htmlFor="cash">Наличные</label>
            </div>

            <div className="payment-method">
              <input
                type="radio"
                id="card"
                name="payment"
                checked={paymentMethod === "card"}
                onChange={() => setPaymentMethod("card")}
              />
              <label htmlFor="card">Картой</label>
            </div>

            {paymentMethod === "card" && (
              <div className="card-details">
                <input
                  type="text"
                  value={cardNumber}
                  onChange={(e) => setCardNumber(e.target.value)}
                  className="checkout-input"
                  placeholder="Номер карты"
                  maxLength="16"
                />
                <input
                  type="text"
                  value={cardName}
                  onChange={(e) => setCardName(e.target.value)}
                  className="checkout-input"
                  placeholder="Имя на карте"
                />
                <div className="card-row">
                  <input
                    type="text"
                    value={cardExpiry}
                    onChange={(e) => setCardExpiry(e.target.value)}
                    className="checkout-input"
                    placeholder="ММ/ГГ"
                    style={{ width: "48%" }}
                    maxLength="5"
                  />
                  <input
                    type="text"
                    value={cardCvv}
                    onChange={(e) => setCardCvv(e.target.value)}
                    className="checkout-input"
                    placeholder="CVV"
                    style={{ width: "48%" }}
                    maxLength="3"
                  />
                </div>
                <div className="save-card">
                  <input
                    type="checkbox"
                    id="saveCard"
                    checked={saveCard}
                    onChange={() => setSaveCard(!saveCard)}
                  />
                  <label htmlFor="saveCard">Сохранить карту</label>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="cart-summary">
        <div className="summary-row">
          <span>Итого:</span>
          <span>{total.toLocaleString()} BYN</span>
        </div>
        <div className="summary-row">
          <span>Доставка:</span>
          <span>Бесплатно</span>
        </div>
        <div className="summary-row">
          <span>Скидка:</span>
          <span>175 BYN</span>
        </div>

        <button
          className="checkout-btn"
          onClick={handlePlaceOrder}
        >
          Заказать
        </button>
      </div>
    </div>
  );
};

export default Checkout;
