import React, { useEffect, useState } from "react";
import "./ProfilePage.css";

const ProfilePage = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
      fetchOrders(storedUser.userId);
    }
  }, []);

  const fetchOrders = async (userId) => {
    try {
      const res = await fetch(`http://localhost:5085/api/orders/user/${userId}`);
      const data = await res.json();

      // Подгрузить pdfUrl для каждого заказа
      const ordersWithPdf = await Promise.all(
        data.map(async (order) => {
          try {
            const pdfRes = await fetch(`http://localhost:5085/api/orders/pdf/${order.orderId}`);
            const pdfData = await pdfRes.json();
            return { ...order, pdfUrl: "http://localhost:5085" + pdfData.pdfUrl };
          } catch {
            return { ...order, pdfUrl: null };
          }
        })
      );

      setOrders(ordersWithPdf);
    } catch (err) {
      console.error("Ошибка при загрузке заказов:", err);
    }
  };

  return (
    <div className="profile-container">
      <h2>Профиль</h2>

      {user && (
        <div className="profile-info">
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Имя пользователя:</strong> {user.username}</p>
        </div>
      )}

      <div className="profile-orders">
        <h3>Ваши заказы</h3>
        {orders.length === 0 ? (
          <p>У вас пока нет заказов.</p>
        ) : (
          orders.map((order) => (
            <div key={order.orderId} className="order-card">
              <p><strong>Номер заказа:</strong> {order.orderId}</p>
              <p><strong>Дата:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
              <p><strong>Сумма:</strong> {order.totalAmount} BYN</p>
              <p><strong>Метод оплаты:</strong> {order.paymentMethod}</p>
              <p><strong>Адрес:</strong> {order.address}</p>
              <p><strong>Телефон:</strong> {order.phone}</p>

              <div className="order-items">
                <strong>Товары:</strong>
                <ul>
                  {order.items?.map((item) => (
                    <li key={item.orderItemId}>
                      {item.product?.brand} {item.product?.model} × {item.quantity} — {item.price * item.quantity} BYN
                    </li>
                  ))}
                </ul>
              </div>

              <div className="order-actions">
                {order.pdfUrl ? (
                  <>
                    <a href={order.pdfUrl} download className="pdf-link">
                      📄 Скачать гарантию
                    </a>
                    <a
                      href={`http://localhost:5085/api/orders/contract/${order.orderId}`}
                      target="_blank"
                      rel="noreferrer"
                      className="pdf-link"
                    >
                      📃 Скачать договор
                    </a>
                  </>
                ) : (
                  <p>Гарантия ещё не готова</p>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
