import React, { useEffect, useState } from "react";
import "./ProfilePage.css";

const ProfilePage = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) return;

    setUser(storedUser);

    fetch(`http://localhost:5085/api/orders/user/${storedUser.userId}`)
      .then((res) => res.json())
      .then(async (orderData) => {
        const parsedOrders = await Promise.all(
          orderData.map(async (order) => {
            let pdfUrl = null;
            let contractUrl = null;

            try {
              const pdfRes = await fetch(`http://localhost:5085/api/orders/pdf/${order.orderId}`);
              const pdfData = await pdfRes.json();
              pdfUrl = "http://localhost:5085" + pdfData.pdfUrl;
            } catch {}

            contractUrl = `http://localhost:5085/api/orders/contract/${order.orderId}`;

            return {
              id: order.orderId,
              date: order.createdAt,
              totalPrice: order.items?.reduce((sum, item) => sum + (item.price * item.quantity), 0) || 0,
              pdfUrl,
              contractUrl,
              items: order.items?.map((item) => ({
                id: item.orderItemId,
                productId: item.product?.productId || item.productId,
                name: item.product?.model || item.product?.name || "Товар",
                image: item.product?.imageUrl || null,
                price: item.price || 0,
                quantity: item.quantity || 1,
              })) || [],
            };
          })
        );
        setOrders(parsedOrders);
      })
      .catch((err) => {
        console.error("Ошибка загрузки заказов:", err);
      });
  }, []);

  return (
    <div className="profile-container">
      <h2>Профиль</h2>
      {user && (
        <div className="profile-info">
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Имя пользователя:</strong> {user.username}</p>
        </div>
      )}

      <div className="profile-orders">
        <h3>Ваши заказы</h3>
        {orders.length === 0 ? (
          <p>У вас пока нет заказов.</p>
        ) : (
          orders.map((order) => (
            <div key={order.id} className="order-card">
              <p><strong>Номер заказа:</strong> {order.id}</p>
              <p><strong>Дата:</strong> {new Date(order.date).toLocaleDateString()}</p>
              <p><strong>Сумма:</strong> {order.totalPrice.toFixed(2)} BYN</p>

              <div className="order-items">
                <strong>Товары:</strong>
                <ul>
                  {order.items.map((item) => (
                    <li key={item.id}>
                      {item.name} × {item.quantity} — {(item.price * item.quantity).toFixed(2)} BYN
                    </li>
                  ))}
                </ul>
              </div>

              <div className="order-actions">
                {order.pdfUrl && (
                  <a href={order.pdfUrl} target="_blank" rel="noreferrer" className="pdf-link">
                    📄 Скачать гарантию
                  </a>
                )}
                {order.contractUrl && (
                  <a href={order.contractUrl} target="_blank" rel="noreferrer" className="pdf-link">
                    📃 Скачать договор
                  </a>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
