// Cart.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Cart.css"; // Создайте отдельный файл для стилей

const Cart = () => {
  const [cartItems, setCartItems] = useState([
    // Пример данных, которые должны приходить из БД
    {
      id: 1,
      name: "Смартфон Apple iPhone 14 128GB",
      screen: "6.1 (2532x1170)",
      cores: 6,
      power: "20 Вт",
      price: 84999,
      discount: 2500,
      quantity: 1,
      available: true
    }
  ]);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [loading, setLoading] = useState(false);

  // Функция для удаления товара
  const handleRemove = async (id) => {
    try {
      // В реальном приложении здесь будет запрос к API
      // await axios.delete(`/api/cart/${id}`);
      setCartItems(cartItems.filter(item => item.id !== id));
    } catch (error) {
      console.error("Ошибка при удалении товара:", error);
    }
  };

  // Функция для изменения количества товара
  const handleQuantityChange = (id, amount) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = Math.max(1, item.quantity + amount);
        return { ...item, quantity: newQuantity };
      }
      return item;
    }));
  };

  // Функция оформления заказа
  const handlePlaceOrder = async () => {
    try {
      // В реальном приложении здесь будет запрос к API
      // await axios.post("/api/orders", { items: cartItems });
      setOrderPlaced(true);
      setCartItems([]);
    } catch (error) {
      console.error("Ошибка при оформлении заказа:", error);
    }
  };

  // Расчет итоговой суммы
  const total = cartItems.reduce(
    (sum, item) => sum + (item.price - (item.discount || 0)) * item.quantity,
    0
  );

  // Если заказ оформлен - показываем сообщение
  if (orderPlaced) {
    return (
      <div className="cart-container">
        <div className="order-success">
          <h2>Заказ успешно оформлен!</h2>
          <p>Спасибо за покупку.</p>
          <button
            className="continue-shopping-btn"
            onClick={() => setOrderPlaced(false)}
          >
            Продолжить покупки
          </button>
        </div>
      </div>
    );
  }

  // Если корзина пуста
  if (cartItems.length === 0) {
    return (
      <div className="cart-container">
        <h2>Моя корзина</h2>
        <div className="cart-empty">
          <p>Ваша корзина пуста</p>
          <button className="catalog-btn">Перейти в каталог</button>
        </div>
      </div>
    );
  }

  // Основной интерфейс корзины
  return (
    <div className="cart-container">
      <h2>Моя корзина</h2>

      {cartItems.map(item => (
        <div key={item.id} className="cart-item">
          <div className="item-info">
            <h3>{item.name}</h3>
            <p>Экран: {item.screen}</p>
            <p>Количество ядер: {item.cores}</p>
            <p>Мощность блока питания: {item.power}</p>
            <div className="availability">{item.available ? "В наличии" : "Нет в наличии"}</div>
          </div>

          <div className="item-price">
            <div className="price-row">
              <span className="original-price">{item.price.toLocaleString()} BYN</span>
              {item.discount > 0 && (
                <span className="discount">- {item.discount.toLocaleString()} BYN</span>
              )}
            </div>

            <div className="quantity-control">
              <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
              <span>{item.quantity}</span>
              <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
            </div>

            <div className="item-total">
              {((item.price - (item.discount || 0)) * item.quantity).toLocaleString()} BYN
            </div>
          </div>

          <button
            className="remove-btn"
            onClick={() => handleRemove(item.id)}
            title="Удалить"
          >
            ×
          </button>
        </div>
      ))}

      <div className="cart-summary">
        <div className="summary-row">
          <span>Итого:</span>
          <span>{total.toLocaleString()} BYN</span>
        </div>
        <button
          className="checkout-btn"
          onClick={handlePlaceOrder}
        >
          Перейти к оформлению
        </button>
      </div>
    </div>
  );
};

export default Cart;
