import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./ComparePage.css";

const ComparePage = () => {
  const [products, setProducts] = useState([]);
  const [specs, setSpecs] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const compareIds = JSON.parse(localStorage.getItem("compare")) || [];

    Promise.all(
      compareIds.map(async (id) => {
        const [productRes, specRes] = await Promise.all([
          fetch(`http://localhost:5085/api/products/${id}`),
          fetch(`http://localhost:5085/api/specifications/${id}`)
        ]);
        const product = await productRes.json();
        const spec = await specRes.json();
        return { product, spec };
      })
    ).then((data) => {
      setProducts(data.map(p => p.product));
      const combined = {};
      data.forEach(({ product, spec }) => {
        spec.forEach(({ attributeName, attributeValue }) => {
          if (!combined[attributeName]) combined[attributeName] = {};
          combined[attributeName][product.productId] = attributeValue;
        });
      });
      setSpecs(combined);
    });
  }, []);

  return (
    <div className="compare-page">
      <h2>Сравнение товаров</h2>
      {products.length < 2 ? (
        <p>Добавьте минимум 2 товара для сравнения</p>
      ) : (
        <table className="compare-table">
          <thead>
            <tr>
              <th>Характеристика</th>
              {products.map(p => (
                <th key={p.productId}>{p.name}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.entries(specs).map(([attr, values]) => (
              <tr key={attr}>
                <td>{attr}</td>
                {products.map(p => (
                  <td key={p.productId}>
                    {values[p.productId] || "—"}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <button className="back-btn" onClick={() => navigate("/")}>Назад</button>
    </div>
  );
};

export default ComparePage;
