import React, { useState, useEffect } from "react";
import "./Cart.css";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) return;

    fetch(`http://localhost:5085/api/cart/${user.userId}`)
      .then((res) => res.json())
      .then(setCartItems)
      .catch((err) => console.error("Ошибка загрузки корзины", err));
  }, []);

  const handleRemove = async (id) => {
    try {
      await fetch(`http://localhost:5085/api/cart/${id}`, { method: "DELETE" });
      setCartItems((prev) => prev.filter((item) => item.id !== id));
    } catch (err) {
      console.error("Ошибка удаления из корзины", err);
    }
  };

  const total = cartItems.reduce(
    (sum, item) => sum + item.quantity * item.product.price,
    0
  );

  return (
    <div className="cart-container">
      <h2>Моя корзина</h2>
      {cartItems.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <>
          <ul className="cart-list">
            {cartItems.map((item) => (
              <li key={item.id} className="cart-item">
                <img
                  src={item.product.images?.[0]?.imageUrl || "/img/placeholder.png"}
                  alt={item.product.name}
                />
                <div className="cart-info">
                  <h3>{item.product.name}</h3>
                  <p>{item.quantity} x {item.product.price} BYN</p>
                </div>
                <button onClick={() => handleRemove(item.id)} className="remove-btn">
                  Удалить
                </button>
              </li>
            ))}
          </ul>
          <div className="cart-total">
            <strong>Итого: {total} BYN</strong>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
