import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import './CategoryPage.css';

const Tablets = () => {
  const allTablets = [
    {
      id: 6,
      img: "/img/tovar/ipad.jpg",
      model: "iPad Pro 11",
      brand: "Apple",
      oldPrice: 1200,
      newPrice: 1000,
      availability: "В наличии",
      memory: "256GB",
      specs: {
        screen: "11'' Liquid Retina",
        ram: "8GB",
        storage: "256GB"
      }
    },
    {
      id: 7,
      img: "/img/tovar/galaxy_tab.jpg",
      model: "Galaxy Tab S9",
      brand: "Samsung",
      oldPrice: 1100,
      newPrice: 900,
      availability: "В наличии",
      memory: "128GB",
      specs: {
        screen: "12.4'' Dynamic AMOLED",
        ram: "12GB",
        storage: "128GB"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    screenSize: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === 'reset') {
      setFilters({
        brand: "",
        screenSize: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  const filteredTablets = allTablets.filter((tablet) => {
    const price = tablet.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;
    return (
      (!filters.brand || tablet.brand === filters.brand) &&
      (!filters.screenSize || tablet.specs.screen.includes(filters.screenSize)) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Планшеты" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог планшетов</h1>
        <div className="product-grid">
          {filteredTablets.map((tablet) => (
            <Link to={`/products/${tablet.id}`} key={tablet.id} className="product-link">
              <ProductCard
                id={tablet.id}
                img={tablet.img}
                model={tablet.model}
                oldPrice={tablet.oldPrice}
                newPrice={tablet.newPrice}
                availability={tablet.availability}
                discount={tablet.oldPrice ? Math.round((1 - tablet.newPrice / tablet.oldPrice) * 100) : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tablets;
