import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProductById } from "../../api";
import '.'
const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    getProductById(productId).then(setProduct);
  }, [productId]);

  if (!product) return <div>Загрузка...</div>;

  return (
    <div className="product-page" style={{ padding: "40px" }}>
      <h1>{product.name}</h1>
      <img
        src={product.images[0]?.imageUrl || "https://via.placeholder.com/400"}
        alt={product.name}
        style={{ width: "400px", height: "auto", borderRadius: "8px" }}
      />
      <p style={{ marginTop: "20px" }}>{product.description}</p>
      <p><strong>Цена:</strong> {product.price} BYN</p>
      <p><strong>В наличии:</strong> {product.quantityInStock}</p>
    </div>
  );
};

export default ProductPage;
