import React, { useEffect, useState } from "react";
import { getProducts } from "../../api";
import { Link } from "react-router-dom";
import "./CategoryPage.css"; // ← обязательно!

const categoryMap = {
  "Смартфоны": 1,
  "Ноутбуки": 2,
  "Планшеты": 3,
  "Телевизоры": 4,
  "Колонки": 5
};

const CategoryPage = ({ category }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts().then((allProducts) => {
      const categoryId = categoryMap[category];
      const filtered = allProducts.filter(p => p.categoryId === categoryId);
      setProducts(filtered);
    });
  }, [category]);

  return (
    <div className="catalog-page">
      <h1>{category}</h1>
      <div className="product-grid">
        {products.map((product) => (
          <Link
            to={`/products/${product.productId}`}
            key={product.productId}
            className="product-card"
          >
            <div className="product-image-container">
              <img
                src={product.images[0]?.imageUrl || "https://via.placeholder.com/300"}
                alt={product.name}
              />
              {product.oldPrice && product.oldPrice > product.price && (
                <div className="discount-badge">
                  -{Math.round(((product.oldPrice - product.price) / product.oldPrice) * 100)}%
                </div>
              )}
              {product.quantityInStock > 0 && (
                <div className="availability-badge">В наличии</div>
              )}
            </div>
            <div className="product-content">
              <h3>{product.name}</h3>
              {product.oldPrice && (
                <p className="old-price">{product.oldPrice.toLocaleString()} ₽</p>
              )}
              <p className="new-price">{product.price.toLocaleString()} ₽</p>
              <p className="description">{product.description.split(",")[0]}</p>
              <button
                className="add-to-cart-btn"
                onClick={(e) => {
                  e.preventDefault();
                  console.log(`Добавлено в корзину: ${product.productId}`);
                }}
              >
                В корзину
              </button>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default CategoryPage;
