import React, { useEffect, useState } from "react";
import axios from "axios";
import "./LoginForm.css";
import { useNavigate } from "react-router-dom";
import Favorites from "../../Components/Favorites/Favorites";

const ProfilePage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [form, setForm] = useState({
    username: "",
    phone: "",
    gender: "",
    birthDate: ""
  });
  const [orders, setOrders] = useState([]);
  const [status, setStatus] = useState("");

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
      return;
    }

    axios.get(`http://localhost:5085/api/auth/${storedUser.userId}`)
      .then(res => {
        setUser(res.data);
        setForm({
          username: res.data.username,
          phone: res.data.phone || "",
          gender: res.data.gender || "",
          birthDate: res.data.birthDate ? res.data.birthDate.slice(0, 10) : ""
        });
        return axios.get(`http://localhost:5085/api/orders/user/${storedUser.userId}`);
      })
      .then(res => setOrders(res.data))
      .catch(() => navigate("/login"));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const updatedUser = {
        userId: user.userId,
        username: form.username,
        phone: form.phone,
        gender: form.gender,
        birthDate: form.birthDate,
        email: user.email,
        passwordHash: user.passwordHash
      };

      await axios.post("http://localhost:5085/api/auth/update", updatedUser);
      setStatus("Профиль обновлён");
    } catch {
      setStatus("Ошибка сохранения");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (!user) return null;

  return (
    <>
      <div className="auth-wrapper">
        <form onSubmit={handleSave} className="auth-form">
          <h2 className="auth-title">Профиль</h2>

          <label className="auth-label">Имя пользователя</label>
          <input
            type="text"
            name="username"
            className="auth-input"
            value={form.username}
            onChange={handleChange}
            required
          />

          <label className="auth-label">Телефон</label>
          <input
            type="text"
            name="phone"
            className="auth-input"
            value={form.phone}
            onChange={handleChange}
          />

          <label className="auth-label">Пол</label>
          <select
            name="gender"
            className="auth-input"
            value={form.gender}
            onChange={handleChange}
          >
            <option value="">Не указан</option>
            <option value="Мужской">Мужской</option>
            <option value="Женский">Женский</option>
          </select>

          <label className="auth-label">Дата рождения</label>
          <input
            type="date"
            name="birthDate"
            className="auth-input"
            value={form.birthDate}
            onChange={handleChange}
          />

          {status && <div className="auth-error">{status}</div>}

          <button type="submit" className="auth-button">Сохранить</button>
          <button
            type="button"
            className="auth-button"
            style={{ backgroundColor: '#ccc', color: '#333' }}
            onClick={handleLogout}
          >
            Выйти из аккаунта
          </button>
        </form>
      </div>

      <div className="auth-wrapper" style={{ marginTop: '20px' }}>
        <div className="auth-form">
          <h2 className="auth-title">Мои заказы</h2>
          {orders.length === 0 ? (
            <p style={{ textAlign: "center" }}>Вы ещё не совершали заказов.</p>
          ) : (
            <ul className="order-list">
              {orders.map(order => (
                <li key={order.orderId} className="order-item">
                  <strong>Заказ №{order.orderId}</strong><br />
                  Дата: {new Date(order.orderDate).toLocaleDateString()}<br />
                  Сумма: {order.total.toLocaleString()} BYN<br />
                  Статус: {order.status}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      <Favorites />
    </>
  );
};

export default ProfilePage;
