import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    console.log('Отправка логина:', { email, password });

    try {
      const response = await axios.post(
        'http://localhost:5085/api/auth/login',
        { email, password },
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      const user = response.data;
      console.log('Успешный вход. Ответ сервера:', user);

      // Проверяем наличие необходимых полей
      if (!user.userId || user.token === undefined) {
        throw new Error('Некорректный ответ сервера: отсутствует userId или token');
      }

      // Сохраняем данные в localStorage
      localStorage.setItem('user', JSON.stringify(user));
      console.log('Сохранено в localStorage:', JSON.parse(localStorage.getItem('user')));

      // Перенаправление в зависимости от роли
      if (user.isAdmin) {
        console.log('Пользователь - админ, перенаправление на /admin');
        navigate('/admin');
      } else {
        console.log('Пользователь - не админ, перенаправление на /profile');
        navigate('/profile');
      }
    } catch (err) {
      console.error('Ошибка логина. Детали:', err.response?.data || err.message);
      const errorMessage =
        err.response?.data?.message ||
        err.response?.statusText ||
        'Не удалось войти. Проверьте данные или сервер.';
      setError(errorMessage);
    }
  };

  return (
    <div className="auth-wrapper">
      <form onSubmit={handleSubmit} className="auth-form">
        <h2 className="auth-title">Вход в аккаунт</h2>

        <label className="auth-label">Email</label>
        <input
          type="email"
          className="auth-input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <label className="auth-label">Пароль</label>
        <input
          type="password"
          className="auth-input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {error && <div className="auth-error">{error}</div>}

        <button type="submit" className="auth-button">Войти</button>
        <button
          type="button"
          className="auth-link"
          onClick={() => navigate('/forgot-password')}
        >
          Забыли пароль?
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
