import React from 'react';
import './ProductCard.css';
const ProductCard = ({
  img,
  model,
  oldPrice,
  newPrice,
  discount,
  isNew,
  availability
}) => {
  return (
    <div className="product-card">
      {/* Бейджи */}
      <div className="badges">...</div>

      {/* Изображение */}
      <div className="product-image">...</div>

      {/* Контент с фиксированной высотой */}
      <div className="card-content">
        <h3 className="product-title">{model}</h3>

        {/* Цены */}
        <div className="price-container">
          {oldPrice && (
            <div className="price-row">
              <span className="old-price">{oldPrice.toLocaleString()} ₽</span>
              <span className="storage">128GB</span>
            </div>
          )}
          <div className="price-row">
            <span className="new-price">{newPrice.toLocaleString()} ₽</span>
            {!oldPrice && <span className="storage">128GB</span>}
          </div>
        </div>
      </div>

      {/* Кнопка фиксирована внизу */}
      <div className="card-footer">
        <span className={`availability ${availability === "В наличии" ? 'in-stock' : 'out-of-stock'}`}>
          {availability}
        </span>
        <button className="cart-button">В корзину</button>
      </div>
    </div>
  );
};

export default ProductCard;
