import React, { useState } from 'react';
import Filters from '../../Components/Filters/Filters';
import '../../Components/Category/Category'; // Create or update this CSS file
import products from "../../data/"

const Smartphones = () => {
  const [filters, setFilters] = useState({
    brand: '',
    ram: '',
    storage: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  // Filter products based on current filters
  const filteredProducts = products.filter(product => {
    return (
      (!filters.brand || product.brand === filters.brand) &&
      (!filters.ram || product.specs.ram === filters.ram) &&
      (!filters.storage || product.specs.storage === filters.storage) &&
      (!filters.minPrice || product.newPrice >= Number(filters.minPrice)) &&
      (!filters.maxPrice || product.newPrice <= Number(filters.maxPrice))
    );
  });

  return (
    <div className="category-page-container">
      <div className="category-layout">
        <div className="filters-column">
          <Filters
            category="Смартфоны"
            filters={filters}
            onFilterChange={handleFilterChange}
          />
        </div>
        <div className="products-column">
          <h2>Смартфоны</h2>
          <div className="products-grid">
            {filteredProducts.length > 0 ? (
              filteredProducts.map(product => (
                <div key={product.id} className="product-card">
                  <img src={process.env.PUBLIC_URL + product.image} alt={product.model} />
                  <h3>{product.model} {product.memory}</h3>
                  <p>{product.newPrice.toLocaleString()} BYN</p>
                  <a href={`/products/${product.id}`}>Подробнее</a>
                </div>
              ))
            ) : (
              <p>Товары не найдены</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
