import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProductById } from "../../api";
import "./ProductPageStyles.css";

const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    getProductById(productId).then(setProduct);
  }, [productId]);

  if (!product) return <div className="error">Загрузка...</div>;

  return (
    <div className="product-page-container">
      <div className="product-main-content">
        <div className="product-left-column">
          <div className="product-image-wrapper">
            <img
              src={product.images[0]?.imageUrl || "https://via.placeholder.com/400"}
              alt={product.name}
              className="product-main-image"
            />
            <div className="availability-badge in-stock">В наличии</div>
          </div>
        </div>

        <div className="product-center-column">
          <div className="product-details">
            <div className="product-info">
              <h1 className="product-title">{product.name}</h1>

              <div className="price-wrapper">
                <div className="new-price">{product.price} BYN</div>
              </div>

              <div className="product-specs">
                <h3>Характеристики</h3>
                <div className="specs-grid">
                  {product.description.split(",").map((item, index) => {
                    const [title, ...rest] = item.split(":");
                    const value = rest.join(":").trim();
                    return (
                      <div className="spec-item" key={index}>
                        <span className="spec-title">{title.trim()}</span>
                        <span className="spec-value">{value}</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="price-floating-container">
          <div className="price-section">
            <div className="product-price">{product.price} BYN</div>
            <button className="add-to-cart-btn">
              Добавить в корзину
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
