import React, { useState, useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import debounce from "lodash.debounce";
import "./Header.css";

const typeLabels = {
  product: "Товар",
  user: "Пользователь",
  category: "Категория",
};

const Header = ({ setCategory }) => {
  const [isCatalogOpen, setIsCatalogOpen] = useState(false);
  const [compareCount, setCompareCount] = useState(0);
  const [cartCount, setCartCount] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const catalogRef = useRef(null);
  const navigate = useNavigate();

  const categories = ["Смартфоны", "Ноутбуки", "Планшеты", "Телевизоры", "Колонки"];

  useEffect(() => {
    const updateCompareCount = () => {
      const compare = JSON.parse(localStorage.getItem("compare")) || [];
      setCompareCount(compare.length);
    };

    updateCompareCount();
    window.addEventListener("storage", updateCompareCount);
    return () => window.removeEventListener("storage", updateCompareCount);
  }, []);

  useEffect(() => {
    const loadCartCount = async () => {
      const user = JSON.parse(localStorage.getItem("user"));
      if (!user?.userId) return;

      try {
        const res = await fetch(`http://localhost:5085/api/cart/${user.userId}`);
        const data = await res.json();
        const items = Array.isArray(data.items) ? data.items : [];
        const totalCount = items.reduce((sum, item) => sum + item.quantity, 0);
        setCartCount(totalCount);
      } catch (err) {
        console.error("Ошибка загрузки корзины", err);
      }
    };

    loadCartCount();
  }, []);

  const fetchSearchResults = debounce(async (query) => {
    if (!query.trim()) {
      setSearchResults([]);
      setShowDropdown(false);
      return;
    }

    try {
      const res = await fetch(`http://localhost:5085/api/search/${query}`);
      const data = await res.json();
      setSearchResults(data);
      setShowDropdown(true);
    } catch (err) {
      console.error("Ошибка при поиске:", err);
    }
  }, 300);

  const handleCategoryClick = (category) => {
    setCategory(category);
    setIsCatalogOpen(false);
  };

  return (
    <header className="header">
      <div className="logo">
        <Link to="/"><h1>COOLZY</h1></Link>
      </div>

      <div className="search">
        <div className="search-container">
          <input
            type="text"
            className="search_box"
            placeholder="ПОИСК"
            value={searchQuery}
            onChange={(e) => {
              const value = e.target.value;
              setSearchQuery(value);
              fetchSearchResults(value);
            }}
            onFocus={() => {
              if (searchResults.length > 0) setShowDropdown(true);
            }}
            onBlur={() => setTimeout(() => setShowDropdown(false), 150)}
          />
          <svg className="search-icon" xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 18 18" fill="none">
            <path d="M8.11111 15.2222C12.0385 15.2222 15.2222 12.0385 15.2222 8.11111C15.2222 4.18375 12.0385 1 8.11111 1C4.18375 1 1 4.18375 1 8.11111C1 12.0385 4.18375 15.2222 8.11111 15.2222Z" stroke="#454545" strokeWidth="2" />
            <path d="M16.9995 17L13.1328 13.1333" stroke="#454545" strokeWidth="2" />
          </svg>

          {showDropdown && (
            <div className="search-dropdown">
              {searchResults.length === 0 && <div style={{ padding: 10 }}>Нет результатов</div>}
              {searchResults
                .filter(item => item.type !== "brand")
                .map((item, idx) => (
                  <div
                    key={idx}
                    onClick={() => {
                      setShowDropdown(false);
                      setSearchQuery("");
                      if (item.type === "product") {
                        navigate(`/products/${item.id}`);
                      } else if (item.type === "user") {
                        navigate(`/profile/${item.id}`);
                      } else if (item.type === "category") {
                        navigate(`/category/${item.name}`);
                      }
                    }}
                    style={{
                      padding: "10px",
                      cursor: "pointer",
                      borderBottom: "1px solid #eee",
                    }}
                  >
                    <span style={{ fontWeight: "bold" }}>{item.name}</span>
                    <span style={{ fontSize: "12px", marginLeft: 10, color: "#666" }}>
                      ({typeLabels[item.type]})
                    </span>
                  </div>
                ))}
            </div>
          )}
        </div>
      </div>

      <nav className="navigation">
        <div className="catalog-wrapper" ref={catalogRef}>
          <button className="catalog" onClick={() => setIsCatalogOpen(!isCatalogOpen)}>
            <svg xmlns="http://www.w3.org/2000/svg" width="26" height="18" viewBox="0 0 26 18" fill="none">
              <rect width="2" height="2" rx="1" fill="#454545" />
              <rect x="6" width="20" height="2" rx="1" fill="#454545" />
              <rect y="8" width="2" height="2" rx="1" fill="#454545" />
              <rect x="6" y="8" width="20" height="2" rx="1" fill="#454545" />
              <rect y="16" width="2" height="2" rx="1" fill="#454545" />
              <rect x="6" y="16" width="20" height="2" rx="1" fill="#454545" />
            </svg>
            <span className="icon_text">Каталог</span>
          </button>

          <div className={`catalog-menu ${isCatalogOpen ? "show" : ""}`}>
            {categories.map((category, index) => (
              <Link to={`/category/${category}`} key={index} className="menu-item" onClick={() => handleCategoryClick(category)}>
                {category}
              </Link>
            ))}
          </div>
        </div>

        <Link to="/compare" className="compare-link" style={{ position: "relative" }}>
          <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" viewBox="0 0 24 24" fill="none">
            <path d="M4 3h2v18H4V3zm7 6h2v12h-2V9zm7-4h2v16h-2V5z" fill="#454545" />
          </svg>
          {compareCount > 0 && (
            <span className="badge">{compareCount}</span>
          )}
          <span className="icon_text">Сравнение</span>
        </Link>

        <Link to="/cart" className="basket" style={{ position: "relative" }}>
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none">
            <path d="M1 1.5H5L7.68 15.7823C7.77 16.2734 8.021 16.7145 8.387 17.0285C8.753 17.3424 9.211 17.5092 9.68 17.4996H19.4C19.87 17.5092 20.327 17.3424 20.6925 17.0285C21.0581 16.7145 21.3086 16.2734 21.4 15.7823L23 6.8332H6" stroke="#454545" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
          </svg>
          {cartCount > 0 && (
            <span className="badge">{cartCount}</span>
          )}
          <span className="icon_text">Корзина</span>
        </Link>

        <Link to="/profile" className="profile">
          <svg xmlns="http://www.w3.org/2000/svg" width="22" height="24" viewBox="0 0 22 24" fill="none">
