import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProductById, addToCart } from "../../api";
import './ProductPageStyles.css';

const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        setError(null);
        const data = await getProductById(productId);
        setProduct(data);
      } catch (err) {
        setError("Не удалось загрузить продукт. Попробуйте позже.");
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleAddToCart = async () => {
    try {
      await addToCart(productId, 1); // Добавляем 1 единицу товара
      alert("Товар добавлен в корзину!");
    } catch (err) {
      alert("Ошибка добавления в корзину. Убедитесь, что вы вошли в систему.");
    }
  };

  if (loading) return <div className="product-page-loading">Загрузка...</div>;
  if (error) return <div className="product-page-error">{error}</div>;
  if (!product) return <div className="product-page-error">Товар не найден.</div>;

  return (
    <div className="product-page">
      <h1 className="product-page-title">{product.name}</h1>
      <div className="product-page-image-container">
        <img
          src={product.productImages?.[0]?.imageUrl || "https://via.placeholder.com/400"}
          alt={product.name}
          className="product-page-image"
        />
      </div>
      <p className="product-page-description">{product.description}</p>
      <p className="product-page-price">
        <strong>Цена:</strong> {product.price.toLocaleString()} BYN
      </p>
      <p className="product-page-stock">
        <strong>В наличии:</strong> {product.quantityInStock > 0 ? product.quantityInStock : "Нет в наличии"}
      </p>
      <button
        className="product-page-add-to-cart"
        onClick={handleAddToCart}
        disabled={product.quantityInStock <= 0}
      >
        Добавить в корзину
      </button>
    </div>
  );
};

export default ProductPage;
