import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProductById } from "../../api";
import "./ProductPageStyles.css";

const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [comment, setComment] = useState("");

  useEffect(() => {
    getProductById(productId).then(setProduct);
    fetch(`http://localhost:5085/api/reviews/${productId}`)
      .then(res => res.json())
      .then(setReviews);
  }, [productId]);

  const handleAddToCart = async () => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) return alert("Сначала войдите в аккаунт");

    try {
      const res = await fetch("http://localhost:5085/api/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user.userId,
          productId: product.id,
          quantity: 1
        })
      });

      const data = await res.json();
      if (data.success) alert("Товар добавлен в корзину");
      else alert("Ошибка при добавлении");
    } catch (error) {
      console.error("Ошибка корзины", error);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.username) return alert("Войдите в аккаунт");

    const review = {
      productId: parseInt(productId),
      userName: user.username,
      comment
    };

  const res = await fetch("http://localhost:5085/api/cart/add", {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({
    userId: user.userId,
    productId: product.id,
    quantity: 1
  })
});

if (!res.ok) {
  const text = await res.text(); // покажем ошибку сервера
  return alert("Ошибка: " + text);
}

let data = {};
try {
  data = await res.json(); // ← здесь теперь не будет падения
} catch {
  data = {};
}

if (data.success) {
  alert("Товар добавлен в корзину");
}


    if (res.ok) {
      setComment("");
      const updated = await fetch(`http://localhost:5085/api/reviews/${productId}`);
      const data = await updated.json();
      setReviews(data);
    }
  };

  if (!product) return <div className="error">Загрузка...</div>;

  return (
    <div className="product-page-container">
      <div className="product-main-content">
        <div className="product-left-column">
          <div className="product-image-wrapper">
            <img
              src={product.images[0]?.imageUrl || "https://via.placeholder.com/400"}
              alt={product.name}
              className="product-main-image"
            />
            <div className="availability-badge in-stock">В наличии</div>
          </div>
        </div>

        <div className="product-center-column">
          <div className="product-details">
            <div className="product-info">
              <h1 className="product-title">{product.name}</h1>

              <div className="price-wrapper">
                <div className="new-price">{product.price} BYN</div>
              </div>

              <div className="product-specs">
                <h3>Характеристики</h3>
                <div className="specs-grid">
                  {product.description.split(",").map((item, index) => {
                    const [title, ...rest] = item.split(":");
                    const value = rest.join(":".trim());
                    return (
                      <div className="spec-item" key={index}>
                        <span className="spec-title">{title.trim()}</span>
                        <span className="spec-value">{value}</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>

          <div className="product-reviews">
            <h3>Отзывы</h3>
            <form onSubmit={handleReviewSubmit}>
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Оставьте ваш отзыв"
                required
                rows="4"
                style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
              />
              <button type="submit" className="add-to-cart-btn">
                Отправить отзыв
              </button>
            </form>

            {reviews.length === 0 ? (
              <p>Отзывов пока нет</p>
            ) : (
              <ul style={{ marginTop: "20px" }}>
                {reviews.map((r) => (
                  <li key={r.id} style={{ marginBottom: "10px" }}>
                    <strong>{r.userName}</strong>: {r.comment}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        <div className="price-floating-container">
          <div className="price-section">
            <div className="product-price">{product.price} BYN</div>
            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              Добавить в корзину
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
