// Header.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const [isCatalogOpen, setIsCatalogOpen] = useState(false);
  const catalogRef = useRef(null);

  const categories = [
    "–≠–ª–µ–∫—Ç—Ä–æ–Ω–∏–∫–∞",
    "–û–¥–µ–∂–¥–∞",
    "–ë—ã—Ç–æ–≤–∞—è —Ç–µ—Ö–Ω–∏–∫–∞",
    "–ö—Ä–∞—Å–æ—Ç–∞",
    "–°–ø–æ—Ä—Ç",
    "–î–µ—Ç—Å–∫–∏–µ —Ç–æ–≤–∞—Ä—ã",
    "–ö–Ω–∏–≥–∏"
  ];

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (catalogRef.current && !catalogRef.current.contains(event.target)) {
        setIsCatalogOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <header className="header">
      <div className="logo">
        <Link to="/">
          <h1>COOLZY</h1>
        </Link>
      </div>

      <div className="search">
        <input
          type="text"
          className="search_box"
          placeholder="üîç –ü–æ–∏—Å–∫"
          aria-label="–ü–æ–∏—Å–∫ –ø–æ —Å–∞–π—Ç—É"
        />
      </div>

      <nav className="navigation">
        <div className="catalog-wrapper" ref={catalogRef}>
          <button
            className="nav-button catalog-trigger"
            onClick={() => setIsCatalogOpen(!isCatalogOpen)}
            aria-expanded={isCatalogOpen}
          >
            <img
              src="/img/icon/catalog_icon.svg"
              alt="–ò–∫–æ–Ω–∫–∞ –∫–∞—Ç–∞–ª–æ–≥–∞"
              width="24"
              height="24"
            />
            <span className="icon-text">–ö–∞—Ç–∞–ª–æ–≥</span>
          </button>

          <div className={`catalog-dropdown ${isCatalogOpen ? 'open' : ''}`}>
            <div className="dropdown-content">
              {categories.map((category, index) => (
                <Link
                  to={`/catalog/${category.toLowerCase().replace(' ', '-')}`}
                  key={index}
                  className="category-link"
                >
                  {category}
                </Link>
              ))}
            </div>
          </div>
        </div>

        {/* –û—Å—Ç–∞–ª—å–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã –Ω–∞–≤–∏–≥–∞—Ü–∏–∏ */}
      </nav>
    </header>
  );
}

export default Header;
