import React, { useState, useEffect } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import "../CategoryPage/CategoryPage";
import { getProductsByCategory } from "../../api"; // используем твой api.jsx

const Laptops = () => {
  const [laptops, setLaptops] = useState([]);
  const [filters, setFilters] = useState({
    brand: "",
    screenSize: "",
    processor: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === "reset") {
      setFilters({
        brand: "",
        screenSize: "",
        processor: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  useEffect(() => {
    const fetchLaptops = async () => {
      const response = await getProductsByCategory("Ноутбуки", filters);
      setLaptops(response);
    };
    fetchLaptops();
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Ноутбуки" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог ноутбуков</h1>
        <div className="product-grid">
          {laptops.map((laptop) => (
            <Link to={`/products/${laptop.id}`} key={laptop.id} className="product-link">
              <ProductCard
                id={laptop.id}
                img={laptop.img}
                model={laptop.model}
                oldPrice={laptop.oldPrice}
                newPrice={laptop.newPrice}
                availability={laptop.availability}
                discount={
                  laptop.oldPrice
                    ? Math.round((1 - laptop.newPrice / laptop.oldPrice) * 100)
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Laptops;
