// Smartphones.jsx
import React, { useEffect, useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
import { Link } from 'react-router-dom';
import './CategoryPage.css';

const Smartphones = () => {
  const [smartphones, setSmartphones] = useState([]);
  const [filters, setFilters] = useState({
    brand: '',
    ram: '',
    storage: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (name, value) => {
    if (name === 'reset') {
      setFilters({
        brand: '',
        ram: '',
        storage: '',
        minPrice: '',
        maxPrice: ''
      });
    } else {
      setFilters(prev => ({ ...prev, [name]: value }));
    }
  };

  useEffect(() => {
    const fetchSmartphones = async () => {
      const query = new URLSearchParams({
        category: 'Смартфоны',
        brand: filters.brand,
        ram: filters.ram,
        storage: filters.storage,
        minPrice: filters.minPrice,
        maxPrice: filters.maxPrice
      });

      // Заглушка для API
      const response = await fetch(`/api/products?${query.toString()}`);
      const data = await response.json();
      setSmartphones(data);
    };

    // Временная заглушка, если API недоступен
    const mockData = [
      {
        id: 1,
        img: "/img/tovar/iphone13.jpg",
        model: "iPhone 14 128GB",
        brand: "Apple",
        oldPrice: 84999,
        newPrice: 79999,
        availability: "В наличии",
        specs: { ram: "6GB", storage: "128GB" }
      },
      {
        id: 2,
        img: "/img/tovar/iphon12.jpg",
        model: "Galaxy S23 256GB",
        brand: "Samsung",
        oldPrice: 85999,
        newPrice: 80999,
        availability: "В наличии",
        specs: { ram: "8GB", storage: "256GB" }
      }
    ];
    setSmartphones(mockData);
    // fetchSmartphones();
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Смартфоны" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог смартфонов</h1>
        <div className="product-grid">
          {smartphones.map(phone => (
            <Link to={`/products/${phone.id}`} key={phone.id} className="product-link">
              <ProductCard
                id={phone.id}
                img={phone.img}
                model={phone.model}
                oldPrice={phone.oldPrice}
                newPrice={phone.newPrice}
                availability={phone.availability}
                discount={phone.oldPrice && phone.oldPrice > phone.newPrice ? Math.round((1 - phone.newPrice / phone.oldPrice) * 100) : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
