// Speakers.jsx
import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
// Общий CSS для категорий
import { Link } from "react-router-dom";

const Speakers = () => {
  const allSpeakers = [
    {
      id: 12,
      img: "/img/tovar/jblgo4.jpg",
      model: "JBL Go 4",
      brand: "JBL",
      oldPrice: 80,
      newPrice: 60,
      availability: "В наличии",
      memory: "N/A",
      specs: {
        screen: "N/A",
        cores: "N/A",
        power: "4.2W",
        ram: "N/A",
        storage: "N/A",
        camera: "N/A"
      }
    },
    {
      id: 13,
      img: "/img/tovar/bose.jpg",
      model: "SoundLink Flex",
      brand: "Bose",
      oldPrice: 150,
      newPrice: 120,
      availability: "В наличии",
      memory: "N/A",
      specs: {
        screen: "N/A",
        cores: "N/A",
        power: "N/A",
        ram: "N/A",
        storage: "N/A",
        camera: "N/A"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    availability: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    setFilters((prev) => ({ ...prev, [filterName]: value }));
  };

  const filteredSpeakers = allSpeakers.filter((speaker) => {
    const price = speaker.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;

    return (
      (!filters.brand || speaker.brand === filters.brand) &&
      (!filters.availability || speaker.availability === filters.availability) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>

      <div className="category-catalog">
        <h1>Каталог колонок</h1>
        <div className="product-grid">
          {filteredSpeakers.map((speaker) => (
            <Link
              to={`/products/${speaker.id}`}
              key={speaker.id}
              className="product-link"
            >
              <ProductCard
                {...speaker}
                discount={
                  speaker.discount
                    ? `${Math.round(
                        (1 - speaker.newPrice / speaker.oldPrice) * 100
                      )}%`
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Speakers;
